#!/usr/bin/env python3
"""
Portal Esot√©rico - Servidor Simples
Vers√£o corrigida para funcionar imediatamente
"""

import http.server
import socketserver
import threading
import signal
import sys
import os
from datetime import datetime

class PortalHandler(http.server.SimpleHTTPRequestHandler):
    def do_GET(self):
        """Handle GET requests"""
        self.send_response(200)
        self.send_header('Content-type', 'text/html; charset=utf-8')
        self.send_header('Access-Control-Allow-Origin', '*')
        self.send_header('Access-Control-Allow-Methods', 'GET, POST, OPTIONS')
        self.send_header('Access-Control-Allow-Headers', 'Content-Type')
        self.send_header('Cache-Control', 'no-cache')
        self.end_headers()

        # Roteamento das p√°ginas
        if self.path == '/blog':
            content = self.get_blog_page()
        elif self.path == '/consultores':
            content = self.get_consultores_page()
        elif self.path == '/creditos':
            content = self.get_creditos_page()
        else:
            content = self.get_home_page()

        self.wfile.write(content.encode('utf-8'))
        print(f"‚úÖ P√°gina servida: {self.path}")

    def get_home_page(self):
        """P√°gina inicial do Portal Esot√©rico"""
        return """
        <!DOCTYPE html>
        <html lang="pt-BR">
        <head>
            <meta charset="UTF-8">
            <meta name="viewport" content="width=device-width, initial-scale=1.0">
            <title>Portal Esot√©rico - Conselhos M√≠sticos</title>
            <style>
                * { margin: 0; padding: 0; box-sizing: border-box; }
                body { 
                    font-family: 'Georgia', serif; 
                    background: linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%);
                    color: #e0e0e0;
                    min-height: 100vh;
                    overflow-x: hidden;
                }
                .container { 
                    max-width: 1200px; 
                    margin: 0 auto; 
                    padding: 20px;
                }
                .header { 
                    text-align: center; 
                    padding: 40px 0;
                    background: rgba(0,0,0,0.3);
                    border-radius: 15px;
                    margin-bottom: 40px;
                    border: 1px solid rgba(255,215,0,0.3);
                }
                .header h1 { 
                    font-size: 3em; 
                    color: #ffd700;
                    text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
                    margin-bottom: 10px;
                }
                .header p { 
                    font-size: 1.3em; 
                    color: #b0c4de;
                    font-style: italic;
                }
                .navigation {
                    display: flex;
                    justify-content: center;
                    gap: 20px;
                    margin: 40px 0;
                }
                .nav-button {
                    background: linear-gradient(45deg, #4a5568, #2d3748);
                    color: #ffd700;
                    padding: 15px 30px;
                    border: none;
                    border-radius: 25px;
                    cursor: pointer;
                    font-size: 1.1em;
                    font-weight: bold;
                    transition: all 0.3s ease;
                    text-decoration: none;
                    border: 2px solid rgba(255,215,0,0.3);
                }
                .nav-button:hover {
                    background: linear-gradient(45deg, #ffd700, #ffed4a);
                    color: #1a1a2e;
                    transform: translateY(-2px);
                    box-shadow: 0 5px 15px rgba(255,215,0,0.4);
                }
                .features {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
                    gap: 30px;
                    margin-top: 50px;
                }
                .feature-card {
                    background: rgba(0,0,0,0.4);
