              </Switch>
            </MainLayout>
          )}
        </Route>
      </Switch>
    </Suspense>
  );
}

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [showContent, setShowContent] = useState(true);

  // Tratamento global de erros para toda a aplica√ß√£o
  useEffect(() => {
    const handleUnhandledRejection = (event: PromiseRejectionEvent) => {
      event.preventDefault();
      console.warn('Global: Unhandled promise rejection prevented:', event.reason);
    };

    const handleError = (event: ErrorEvent) => {
      event.preventDefault();
      console.warn('Global: Error event caught:', event.error);
    };

    window.addEventListener('unhandledrejection', handleUnhandledRejection);
    window.addEventListener('error', handleError);

    // Start loading timer - force show content after 3 seconds max
    const timer = setTimeout(() => {
      if (isLoading) {
        setIsLoading(false);
        setShowContent(true);
      }
    }, 3000);

    return () => {
      window.removeEventListener('unhandledrejection', handleUnhandledRejection);
      window.removeEventListener('error', handleError);
      clearTimeout(timer);
    };
  }, [isLoading]);

  const handleLoadingComplete = () => {
    setIsLoading(false);
    setTimeout(() => setShowContent(true), 300);
  };

  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        {isLoading && <LoadingScreen onComplete={handleLoadingComplete} />}
        {showContent && <Router />}

        {/* PWA functionality handled by Service Worker in main.tsx */}
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

