@tailwind base;
@tailwind components;
@tailwind utilities;

/* PWA and Mobile Optimizations */
@layer base {
  html {
    -webkit-text-size-adjust: 100%;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    scroll-behavior: smooth;
  }

  body {
    overscroll-behavior: none;
    -webkit-overflow-scrolling: touch;
    -webkit-user-select: none;
    -webkit-touch-callout: none;
    -webkit-tap-highlight-color: transparent;
  }

  /* Safe area for notched devices */
  @supports (padding: max(0px)) {
    body {
      padding-left: env(safe-area-inset-left);
      padding-right: env(safe-area-inset-right);
    }
  }
}

/* Custom gradient for loading screen */
@layer utilities {
  .bg-gradient-radial {
    background: radial-gradient(circle, var(--tw-gradient-stops));
  }

  /* PWA specific utilities */
  .pwa-safe-top {
    padding-top: env(safe-area-inset-top);
  }

  .pwa-safe-bottom {
    padding-bottom: env(safe-area-inset-bottom);
  }

  .pwa-safe-left {
    padding-left: env(safe-area-inset-left);
  }

  .pwa-safe-right {
    padding-right: env(safe-area-inset-right);
  }
}

@layer base {
  :root {
    /* Cores do Portal Esot√©rico */
    --dark-blue: #1a1a2e;
    --gold: #ffd700;
    --green: #22c55e;
    --mystic-purple: #6b46c1;
    --deep-purple: #4c1d95;
  }
}

@layer utilities {
  .text-dark-blue { color: var(--dark-blue); }
  .bg-dark-blue { background-color: var(--dark-blue); }
  .text-gold { color: var(--gold); }
  .bg-gold { background-color: var(--gold); }
  .text-green { color: var(--green); }
  .bg-green { background-color: var(--green); }
  .text-mystic-purple { color: var(--mystic-purple); }
  .bg-mystic-purple { background-color: var(--mystic-purple); }
  .text-deep-purple { color: var(--deep-purple); }
  .bg-deep-purple { background-color: var(--deep-purple); }
}

/* Reset e configura√ß√µes base */
body {
  font-family: 'Georgia', serif;
  background-color: #f8fafc;
}

/* Estilos espec√≠ficos para evitar conflitos */
.container {
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}

/* Estilos base para elementos */
h1, h2, h3, h4, h5, h6 {
  margin: 0;
  padding: 0;
}

img {
  max-width: 100%;
  height: auto;
}

button {
  border: none;
  outline: none;
  cursor: pointer;
}

/* Garantir consist√™ncia de renderiza√ß√£o */
* {
  box-sizing: border-box;
}

