import { useEffect, useState } from 'react';

export function SimpleSplash() {
  const [mounted, setMounted] = useState(false);
  const [show, setShow] = useState(false);

  useEffect(() => {
    setMounted(true);
    setShow(true);

    const timer = setTimeout(() => {
