import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Star, Sparkles, Heart, ChevronLeft, ChevronRight } from 'lucide-react';
import { useQuery } from '@tanstack/react-query';
import { Link } from 'wouter';

interface BannerSlide {
  id: number;
  title: string;
  subtitle?: string;
  description?: string;
  imageUrl: string;
  buttonText?: string;
  buttonLink?: string;
  textPosition: 'center' | 'left' | 'right';
  textAlignment: 'center' | 'left' | 'right';
  titleColor: string;
  subtitleColor: string;
  descriptionColor: string;
  backgroundOverlay: string;
  buttonStyle: 'primary' | 'secondary' | 'accent';
  buttonPosition: 'center' | 'left' | 'right';
  autoRotateSeconds: number;
  isActive: boolean;
  order: number;
}

export function AdvancedHeroBanner() {
  const { data: banners, isLoading } = useQuery({
    queryKey: ['/api/banners/active'],
  });

  const [currentSlide, setCurrentSlide] = useState(0);

  // Converter para array tipado
  const bannerArray = Array.isArray(banners) ? banners as BannerSlide[] : [];

  // Auto-rotate slides com intervalo mais curto para teste
  useEffect(() => {
    if (bannerArray.length <= 1) return;

    const currentBanner = bannerArray[currentSlide];
    const rotateSeconds = currentBanner?.autoRotateSeconds || 3; // Reduzido para 3 segundos

    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % bannerArray.length);
    }, rotateSeconds * 1000);

    return () => clearInterval(timer);
  }, [bannerArray, currentSlide]);

  if (isLoading) {
    return (
      <section className="relative h-[600px] bg-gradient-to-br from-primary to-accent flex items-center justify-center">
        <div className="text-white text-center">
          <div className="animate-spin w-8 h-8 border-4 border-white border-t-transparent rounded-full mx-auto mb-4"></div>
          <p>Carregando banner...</p>
        </div>
      </section>
    );
  }

  if (bannerArray.length === 0) {
    // Banners de demonstra√ß√£o quando n√£o h√° dados do backend
    const defaultBanners: BannerSlide[] = [
      {
        id: 1,
        title: "Conecte-se com o Espiritual",
        subtitle: "Portal de Consultas M√≠sticas",
        description: "Orienta√ß√£o espiritual aut√™ntica com nossos consultores especializados em Tarot, Astrologia e Mediunidade",
        imageUrl: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=1920&h=600&fit=crop",
        buttonText: "Encontrar Consultor",
        buttonLink: "/consultores",
        textPosition: "center",
        textAlignment: "center",
        titleColor: "#FFFFFF",
        subtitleColor: "#FDE68A",
        descriptionColor: "#F3F4F6",
        backgroundOverlay: "rgba(0,0,0,0.5)",
        buttonStyle: "accent",
        buttonPosition: "center",
        autoRotateSeconds: 4,
        isActive: true,
        order: 1
      },
      {
        id: 2,
        title: "Tarot Gr√°tis Dispon√≠vel",
        subtitle: "Leitura Online Imediata",
        description: "Descubra insights sobre seu futuro com nossa ferramenta de Tarot gratuita",
        imageUrl: "https://images.unsplash.com/photo-1541963463532-d68292c34d19?w=1920&h=600&fit=crop",
        buttonText: "Jogar Tarot",
        buttonLink: "/tarot-gratis",
        textPosition: "left",
        textAlignment: "left",
        titleColor: "#FFFFFF",
        subtitleColor: "#DDD6FE",
        descriptionColor: "#E5E7EB",
        backgroundOverlay: "rgba(75,0,130,0.6)",
        buttonStyle: "secondary",
        buttonPosition: "left",
        autoRotateSeconds: 4,
        isActive: true,
        order: 2
      },
      {
        id: 3,
        title: "Especialistas Certificados",
        subtitle: "Qualidade Garantida",
        description: "Consultores experientes prontos para guiar voc√™ em sua jornada espiritual",
        imageUrl: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=1920&h=600&fit=crop",
        buttonText: "Ver Consultores",
        buttonLink: "/consultores",
        textPosition: "right",
        textAlignment: "right",
        titleColor: "#FFFFFF",
        subtitleColor: "#FCD34D",
        descriptionColor: "#F9FAFB",
        backgroundOverlay: "rgba(139,69,19,0.5)",
        buttonStyle: "primary",
        buttonPosition: "right",
        autoRotateSeconds: 4,
        isActive: true,
        order: 3
      }
    ];

    const demonstrationBanners = defaultBanners;
    const currentBanner = demonstrationBanners[currentSlide % demonstrationBanners.length];

    // Auto-rotate para banners de demonstra√ß√£o
    useEffect(() => {
      const timer = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % demonstrationBanners.length);
      }, 4000);
      return () => clearInterval(timer);
    }, []);

    return (
      <section className="relative h-[600px] overflow-hidden">
        <AnimatePresence mode="wait">
          <motion.div
