                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.8, delay: 0.5 }}
                >
                  {currentBanner.description}
                </motion.p>

                <motion.div 
                  className={`flex ${
                    currentBanner.buttonPosition === 'left' ? 'justify-start' :
                    currentBanner.buttonPosition === 'right' ? 'justify-end' :
                    'justify-center'
                  }`}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.8, delay: 0.6 }}
                >
                  <Link href={currentBanner.buttonLink || "/consultores"}>
                    <motion.button
                      className={`${
                        currentBanner.buttonStyle === 'secondary' 
                          ? 'bg-white/20 hover:bg-white/30 text-white font-semibold py-4 px-8 rounded-full text-lg transition-all duration-300 hover:scale-105 backdrop-blur-sm border border-white/30'
                          : currentBanner.buttonStyle === 'accent'
