        {/* Indicadores */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2 z-20">
          {demonstrationBanners.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentSlide(index)}
              className={`w-3 h-3 rounded-full transition-all duration-300 hover:scale-125 ${
                index === currentSlide ? 'bg-white' : 'bg-white/50'
              }`}
            />
          ))}
        </div>
      </section>
    );
  }

  // Banner padr√£o antigo removido
  if (false) {
    return (
      <section className="relative h-[600px] bg-gradient-to-br from-primary via-purple-700 to-accent overflow-hidden">
        <div className="absolute inset-0 bg-black/20"></div>

        {/* Elementos decorativos */}
        <div className="absolute top-20 left-10 text-white/20">
          <Sparkles size={24} />
        </div>
        <div className="absolute top-40 right-20 text-white/20">
          <Star size={20} />
        </div>
        <div className="absolute bottom-32 left-20 text-white/20">
          <Heart size={18} />
        </div>

        <div className="container mx-auto px-4 h-full flex items-center justify-center relative z-10">
          <motion.div
            className="text-center text-white max-w-4xl"
