  }

  const currentBanner = bannerArray[currentSlide];

  // Fun√ß√£o para obter classes de posicionamento de texto
  const getTextPositionClasses = (position: string) => {
    switch (position) {
      case 'left':
        return 'items-center justify-start';
      case 'right':
        return 'items-center justify-end';
      default:
        return 'items-center justify-center';
    }
  };

  // Fun√ß√£o para obter classes de alinhamento de texto
  const getTextAlignmentClasses = (alignment: string) => {
    switch (alignment) {
      case 'left':
        return 'text-left';
      case 'right':
        return 'text-right';
      default:
        return 'text-center';
    }
  };

  // Fun√ß√£o para obter classes do bot√£o
  const getButtonClasses = (style: string) => {
    switch (style) {
      case 'secondary':
