                  className="text-xl md:text-2xl mb-8 max-w-2xl"
                  style={{ 
                    color: currentBanner.descriptionColor || '#F3F4F6',
                    marginLeft: currentBanner.textAlignment === 'center' ? 'auto' : '0',
                    marginRight: currentBanner.textAlignment === 'center' ? 'auto' : '0'
