client/src/components/ConsultationChat.t.content:
import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Send, Phone, Video, FileText, Image, Smile, MoreVertical } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { useToast } from '@/hooks/use-toast';

interface Message {
  id: number;
  senderId: string;
  senderType: 'client' | 'consultant';
  content: string;
  messageType: 'text' | 'image' | 'file';
  timestamp: Date;
}

interface ConsultationChatProps {
  roomId: string;
  userId: string;
  userType: 'client' | 'consultant';
  consultantName?: string;
  consultantImage?: string;
  onEndSession?: () => void;
}

export const ConsultationChat: React.FC<ConsultationChatProps> = ({
  roomId,
  userId,
  userType,
  consultantName = "Consultor",
  consultantImage,
  onEndSession
}) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [typingUser, setTypingUser] = useState<string | null>(null);
  const [ws, setWs] = useState<WebSocket | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();

  useEffect(() => {
    connectWebSocket();
    return () => {
      if (ws) {
        ws.close();
      }
    };
  }, [roomId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const connectWebSocket = () => {
    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws/consultation`;

    const websocket = new WebSocket(wsUrl);

    websocket.onopen = () => {
      console.log('WebSocket connected');
      setIsConnected(true);
      setWs(websocket);

      // Join room
      websocket.send(JSON.stringify({
        type: 'join',
        roomId,
        userId,
        userType
      }));
    };

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      handleWebSocketMessage(data);
    };

    websocket.onclose = () => {
      console.log('WebSocket disconnected');
      setIsConnected(false);
      setWs(null);
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      toast({
        title: "Erro de Conex√£o",
        description: "Problemas na conex√£o. Tentando reconectar...",
        variant: "destructive"
      });
    };
  };

  const handleWebSocketMessage = (data: any) => {
    switch (data.type) {
      case 'joined':
        toast({
          title: "Conectado",
          description: "Voc√™ entrou na sala de consulta"
        });
        break;

      case 'message_history':
        setMessages(data.messages.map((msg: any) => ({
          ...msg,
          timestamp: new Date(msg.timestamp)
        })));
        break;

      case 'new_message':
        setMessages(prev => [...prev, {
          id: data.messageId,
          senderId: data.senderId,
          senderType: data.senderType,
          content: data.content,
          messageType: data.messageType,
          timestamp: new Date(data.timestamp)
        }]);
        break;

      case 'user_typing':
        if (data.userId !== userId) {
          setTypingUser(data.userId);
        }
        break;

      case 'user_stop_typing':
        setTypingUser(null);
        break;

      case 'session_ended':
        toast({
          title: "Consulta Finalizada",
          description: "A sess√£o foi encerrada"
        });
        if (onEndSession) {
          onEndSession();
        }
        break;

      case 'error':
        toast({
          title: "Erro",
          description: data.message,
          variant: "destructive"
        });
        break;
    }
  };

  const sendMessage = () => {
    if (!newMessage.trim() || !ws || !isConnected) return;

    ws.send(JSON.stringify({
      type: 'message',
      roomId,
      userId,
      userType,
      content: newMessage,
      messageType: 'text'
    }));

    setNewMessage('');
  };

  const handleTyping = () => {
    if (!ws || !isConnected) return;

    ws.send(JSON.stringify({
      type: 'typing',
      roomId,
      userId,
      userType
    }));

    // Stop typing after 3 seconds
    setTimeout(() => {
      if (ws && isConnected) {
        ws.send(JSON.stringify({
          type: 'stop_typing',
          roomId,
          userId,
          userType
        }));
      }
    }, 3000);
  };

  const endSession = () => {
    if (!ws || !isConnected) return;

    ws.send(JSON.stringify({
      type: 'end_session',
      roomId,
      userId,
      userType
    }));
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('pt-BR', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-lg">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b bg-gradient-to-r from-primary to-accent text-white rounded-t-lg">
        <div className="flex items-center space-x-3">
          <Avatar>
            <AvatarImage src={consultantImage} />
            <AvatarFallback>{consultantName.charAt(0)}</AvatarFallback>
          </Avatar>
          <div>
            <h3 className="font-semibold">{consultantName}</h3>
            <p className="text-sm opacity-90">
              {isConnected ? 'Online' : 'Conectando...'}
            </p>
          </div>
        </div>

        <div className="flex space-x-2">
          <Button variant="ghost" size="sm" className="text-white hover:bg-white/20">
            <Phone className="w-4 h-4" />
          </Button>
          <Button variant="ghost" size="sm" className="text-white hover:bg-white/20">
            <Video className="w-4 h-4" />
          </Button>
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={endSession}
            className="text-white hover:bg-white/20"
          >
            <MoreVertical className="w-4 h-4" />
          </Button>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        <AnimatePresence>
          {messages.map((message) => (
            <motion.div
              key={message.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              className={`flex ${
                message.senderId === userId ? 'justify-end' : 'justify-start'
              }`}
            >
              <div
                className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                  message.senderId === userId
                    ? 'bg-primary text-white'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                <p className="text-sm">{message.content}</p>
                <p className={`text-xs mt-1 ${
                  message.senderId === userId ? 'text-white/70' : 'text-gray-500'
                }`}>
                  {formatTime(message.timestamp)}
                </p>
              </div>
            </motion.div>
          ))}
        </AnimatePresence>

        {/* Typing indicator */}
        {typingUser && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="flex justify-start"
          >
            <div className="bg-gray-100 px-4 py-2 rounded-lg">
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
              </div>
            </div>
          </motion.div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="p-4 border-t">
        <div className="flex space-x-2">
          <Button variant="ghost" size="sm">
            <FileText className="w-4 h-4" />
          </Button>
          <Button variant="ghost" size="sm">
            <Image className="w-4 h-4" />
          </Button>
          <Button variant="ghost" size="sm">
            <Smile className="w-4 h-4" />
          </Button>
          <Input
            value={newMessage}
            onChange={(e) => {
              setNewMessage(e.target.value);
              handleTyping();
            }}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                sendMessage();
              }
            }}
            placeholder="Digite sua mensagem..."
            className="flex-1"
            disabled={!isConnected}
          />
          <Button 
            onClick={sendMessage}
            disabled={!newMessage.trim() || !isConnected}
            className="bg-primary hover:bg-primary/90"
          >
            <Send className="w-4 h-4" />
          </Button>
        </div>
      </div>
    </div>
  );
};

client/src/components/ConsultationNotifi.content:
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Bell, 
  X, 
  CheckCircle, 
  AlertCircle, 
  MessageCircle,
  Users,
  Clock
} from 'lucide-react';
import { useQuery } from '@tanstack/react-query';

interface Notification {
  id: number;
  type: 'success' | 'info' | 'warning';
  title: string;
  message: string;
  timestamp: string;
  isRead: boolean;
  action?: {
    text: string;
    url: string;
  };
}

export function ConsultationNotifications() {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);

  // Buscar estat√≠sticas para gerar notifica√ß√µes din√¢micas
  const { data: stats } = useQuery({
    queryKey: ['/api/consultation/stats'],
    refetchInterval: 5000,
  });

  // Simular notifica√ß√µes em tempo real baseadas nas estat√≠sticas
  useEffect(() => {
    if (stats) {
      const currentTime = new Date().toLocaleTimeString();

      // Gerar notifica√ß√µes baseadas em dados reais
      const dynamicNotifications: Notification[] = [];

      if (stats.onlineConsultants > 0) {
        dynamicNotifications.push({
          id: Date.now() + 1,
          type: 'success',
          title: 'Consultores Online',
          message: `${stats.onlineConsultants} especialistas dispon√≠veis agora`,
          timestamp: currentTime,
          isRead: false,
          action: {
            text: 'Ver Consultores',
            url: '/consultas-online'
          }
        });
      }

      if (stats.activeConsultations > 0) {
        dynamicNotifications.push({
          id: Date.now() + 2,
          type: 'info',
          title: 'Consultas Ativas',
          message: `${stats.activeConsultations} sess√µes em andamento`,
          timestamp: currentTime,
          isRead: false
        });
      }

      if (stats.queueLength > 0) {
        dynamicNotifications.push({
          id: Date.now() + 3,
          type: 'warning',
          title: 'Fila de Espera',
          message: `${stats.queueLength} pessoa(s) aguardando atendimento`,
          timestamp: currentTime,
          isRead: false
        });
      }

      // Atualizar notifica√ß√µes evitando duplicatas
      setNotifications(prev => {
        const existingIds = prev.map(n => n.type + n.title);
        const newNotifications = dynamicNotifications.filter(
          n => !existingIds.includes(n.type + n.title)
        );

        const updated = [...newNotifications, ...prev].slice(0, 5); // Manter apenas 5 mais recentes
        setUnreadCount(updated.filter(n => !n.isRead).length);
        return updated;
      });
    }
  }, [stats]);

  const markAsRead = (id: number) => {
    setNotifications(prev => 
      prev.map(n => n.id === id ? { ...n, isRead: true } : n)
    );
    setUnreadCount(prev => Math.max(0, prev - 1));
  };

  const markAllAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, isRead: true })));
    setUnreadCount(0);
  };

  const getIcon = (type: string) => {
    switch (type) {
      case 'success':
        return <CheckCircle className="w-5 h-5 text-green-500" />;
      case 'warning':
        return <AlertCircle className="w-5 h-5 text-orange-500" />;
      default:
        return <MessageCircle className="w-5 h-5 text-blue-500" />;
    }
  };

  return (
    <div className="fixed top-4 right-4 z-50">
      {/* Bot√£o de Notifica√ß√µes */}
      <motion.button
        onClick={() => setIsOpen(!isOpen)}
        className="relative bg-white p-3 rounded-full shadow-lg border-2 border-purple-100 hover:border-purple-300 transition-all duration-300"
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        <Bell className="w-6 h-6 text-purple-600" />
        {unreadCount > 0 && (
          <motion.div
            className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-6 h-6 flex items-center justify-center"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            {unreadCount}
          </motion.div>
        )}
      </motion.button>

      {/* Painel de Notifica√ß√µes */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            className="absolute top-16 right-0 w-96 bg-white rounded-xl shadow-2xl border border-gray-200 overflow-hidden"
            initial={{ opacity: 0, y: -20, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: -20, scale: 0.95 }}
            transition={{ duration: 0.2 }}
          >
            {/* Header */}
            <div className="bg-gradient-to-r from-purple-600 to-blue-600 p-4 text-white">
              <div className="flex items-center justify-between">
                <h3 className="font-semibold text-lg">Notifica√ß√µes</h3>
                <div className="flex items-center space-x-2">
                  {unreadCount > 0 && (
                    <button
                      onClick={markAllAsRead}
                      className="text-sm bg-white/20 px-3 py-1 rounded-full hover:bg-white/30 transition-colors"
                    >
                      Marcar todas como lidas
                    </button>
                  )}
                  <button
                    onClick={() => setIsOpen(false)}
                    className="p-1 hover:bg-white/20 rounded-full transition-colors"
                  >
                    <X className="w-5 h-5" />
                  </button>
                </div>
              </div>
            </div>

            {/* Lista de Notifica√ß√µes */}
            <div className="max-h-96 overflow-y-auto">
              {notifications.length === 0 ? (
                <div className="p-8 text-center text-gray-500">
                  <Bell className="w-12 h-12 mx-auto mb-3 text-gray-300" />
                  <p>Nenhuma notifica√ß√£o no momento</p>
                </div>
              ) : (
                notifications.map((notification) => (
                  <motion.div
                    key={notification.id}
                    className={`p-4 border-b border-gray-100 hover:bg-gray-50 transition-colors cursor-pointer ${
                      !notification.isRead ? 'bg-purple-50' : ''
                    }`}
                    onClick={() => markAsRead(notification.id)}
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    <div className="flex items-start space-x-3">
                      <div className="flex-shrink-0 mt-1">
                        {getIcon(notification.type)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center space-x-2">
                          <h4 className="font-medium text-gray-900 truncate">
                            {notification.title}
                          </h4>
                          {!notification.isRead && (
                            <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
                          )}
                        </div>
                        <p className="text-sm text-gray-600 mt-1">
                          {notification.message}
                        </p>
                        <div className="flex items-center justify-between mt-2">
                          <span className="text-xs text-gray-400">
                            {notification.timestamp}
                          </span>
                          {notification.action && (
                            <a
                              href={notification.action.url}
                              className="text-xs text-purple-600 hover:text-purple-800 font-medium"
                              onClick={(e) => e.stopPropagation()}
                            >
                              {notification.action.text}
                            </a>
                          )}
                        </div>
                      </div>
                    </div>
                  </motion.div>
                ))
              )}
            </div>

            {/* Estat√≠sticas R√°pidas */}
            {stats && (
              <div className="bg-gray-50 p-4 border-t">
                <div className="grid grid-cols-3 gap-4 text-center">
                  <div>
                    <Users className="w-4 h-4 text-green-500 mx-auto mb-1" />
                    <div className="text-sm font-semibold text-gray-900">{stats.onlineConsultants}</div>
                    <div className="text-xs text-gray-500">Online</div>
                  </div>
                  <div>
                    <MessageCircle className="w-4 h-4 text-blue-500 mx-auto mb-1" />
                    <div className="text-sm font-semibold text-gray-900">{stats.activeConsultations}</div>
                    <div className="text-xs text-gray-500">Ativas</div>
                  </div>
                  <div>
                    <Clock className="w-4 h-4 text-orange-500 mx-auto mb-1" />
                    <div className="text-sm font-semibold text-gray-900">{stats.queueLength}</div>
                    <div className="text-xs text-gray-500">Na Fila</div>
                  </div>
                </div>
              </div>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

