  backgroundImage: string;
  primaryButton: {
    text: string;
    link: string;
    style: "primary" | "secondary";
  };
  secondaryButton: {
    text: string;
    link: string;
    style: "primary" | "secondary";
  };
  stats: {
    clients: string;
    consultants: string;
    availability: string;
  };
}

const bannerSlides: BannerSlide[] = [
  {
    id: 1,
