                <motion.div
                  key={i}
                  className={`absolute text-white/10 ${isSmall ? 'hidden md:block' : ''}`}
                  style={{
                    top: `${Math.random() * 100}%`,
                    left: `${Math.random() * 100}%`,
                    fontSize: `${size}px`,
                    rotate: `${Math.random() * 360}deg`,
                  }}
