import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
// Logo oficial do Conselhos Esot√©ricos
import logoImage from "@assets/CONSELHOS_20250521_110746_0000_1753966138984.png";

interface LoadingScreenProps {
  onComplete: () => void;
}

export const LoadingScreen: React.FC<LoadingScreenProps> = ({ onComplete }) => {
  const [progress, setProgress] = useState(0);

  const serviceIcons = [
    { icon: "üîÆ", name: "Tarot" },
    { icon: "‚≠ê", name: "Astrologia" },
    { icon: "üßø", name: "Or√°culos" },
    { icon: "üåô", name: "Mediunidade" },
    { icon: "üî¢", name: "Numerologia" },
    { icon: "‚ú®", name: "Reiki" },
    { icon: "üíé", name: "Cristais" },
    { icon: "üåø", name: "Runas" },
    { icon: "ü¶ã", name: "Terapias" },
    { icon: "üé¥", name: "Cartomancia" },
    { icon: "üåü", name: "Energia" },
    { icon: "üïØÔ∏è", name: "Rituais" }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          clearInterval(timer);
          setTimeout(onComplete, 800);
          return 100;
        }
        return prev + 1.5;
      });
    }, 60);

    return () => clearInterval(timer);
  }, [onComplete]);

  return (
    <AnimatePresence>
      <motion.div
        className="fixed inset-0 z-50 flex items-center justify-center bg-gradient-to-br from-white via-purple-50 to-white"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0, scale: 1.1 }}
