import React, { useState } from 'react';
import { useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Eye, EyeOff, User, Crown, CheckCircle2 } from 'lucide-react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import logoImage from "@assets/CONSELHOS_20250521_110746_0000_1754078656294.png";

const Cadastro = () => {
  const [, setLocation] = useLocation();
  const [selectedRole, setSelectedRole] = useState('cliente');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    cpf: '',
    phone: '',
    // Campos espec√≠ficos para consultores
    specialty: '',
    experience: '',
    description: '',
    pricePerMinute: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    setSuccess('');

    // Valida√ß√µes
    if (formData.password !== formData.confirmPassword) {
      setError('As senhas n√£o coincidem');
      setIsLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('A senha deve ter pelo menos 6 caracteres');
      setIsLoading(false);
      return;
    }

    const cpfNumbers = formData.cpf.replace(/\D/g, '');
    if (!cpfNumbers || cpfNumbers.length !== 11) {
      setError('CPF √© obrigat√≥rio e deve ter 11 d√≠gitos');
      setIsLoading(false);
      return;
    }

    const phoneNumbers = formData.phone.replace(/\D/g, '');
    if (!phoneNumbers || phoneNumbers.length < 10) {
      setError('Telefone √© obrigat√≥rio e deve ter pelo menos 10 d√≠gitos');
      setIsLoading(false);
      return;
    }

    if (selectedRole === 'consultor') {
      if (!formData.specialty) {
        setError('Especialidade √© obrigat√≥ria para consultores');
        setIsLoading(false);
        return;
      }
      if (!formData.experience) {
        setError('Experi√™ncia √© obrigat√≥ria para consultores');
        setIsLoading(false);
        return;
      }
      if (!formData.description) {
        setError('Descri√ß√£o √© obrigat√≥ria para consultores');
        setIsLoading(false);
        return;
      }
      if (!formData.pricePerMinute || parseFloat(formData.pricePerMinute) <= 0) {
        setError('Pre√ßo por minuto deve ser maior que zero');
        setIsLoading(false);
        return;
      }
    }

    try {
      const response = await fetch('/api/test/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          password: formData.password,
          cpf: formData.cpf.replace(/\D/g, ''), // Envia apenas n√∫meros
          phone: formData.phone.replace(/\D/g, ''), // Envia apenas n√∫meros
          role: selectedRole,
          // Dados espec√≠ficos para consultores
          specialty: selectedRole === 'consultor' ? formData.specialty : undefined,
          experience: selectedRole === 'consultor' ? formData.experience : undefined,
          description: selectedRole === 'consultor' ? formData.description : undefined,
          pricePerMinute: selectedRole === 'consultor' ? formData.pricePerMinute : undefined,
        }),
      });

      const data = await response.json();

      if (data.success) {
        setSuccess(data.message);

        if (selectedRole === 'cliente') {
          // Cliente pode fazer login imediatamente
          localStorage.setItem('authToken', data.token);
          setTimeout(() => {
            setLocation('/client-dashboard');
          }, 2000);
        } else {
          // Consultor precisa aguardar aprova√ß√£o
          setTimeout(() => {
            setLocation('/login');
          }, 3000);
        }
      } else {
        setError(data.error || 'Erro ao criar conta');
      }
    } catch (err) {
      setError('Erro de conex√£o com o servidor');
    } finally {
      setIsLoading(false);
    }
  };

  const roleConfig = {
    cliente: {
      icon: User,
      color: 'bg-amber-500',
      hoverColor: 'hover:bg-amber-600',
      borderColor: 'border-amber-500',
      textColor: 'text-amber-600',
      title: 'Cliente',
      description: 'Acesso √†s consultas e servi√ßos esot√©ricos',
      benefits: ['Consultas por chat e v√≠deo', 'Cr√©ditos de b√¥nus', 'Hist√≥rico de consultas', 'Avalia√ß√µes e feedback']
    },
    consultor: {
      icon: Crown,
      color: 'bg-purple-600',
      hoverColor: 'hover:bg-purple-700',
      borderColor: 'border-purple-600',
      textColor: 'text-purple-600',
      title: 'Consultor',
      description: 'Ofere√ßa seus servi√ßos esot√©ricos na plataforma',
      benefits: ['Painel de consultoria', 'Gest√£o de hor√°rios', 'Recebimento de pagamentos', 'Sistema de avalia√ß√µes']
    }
  };

  const currentConfig = roleConfig[selectedRole as keyof typeof roleConfig];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-amber-50 flex items-center justify-center p-4">
      <div className="w-full max-w-lg">
        <Card className="shadow-xl border-0">
          <CardHeader className="text-center space-y-4">
            <div className="mx-auto w-32 h-24 flex items-center justify-center">
              <img 
                src={logoImage} 
                alt="Conselhos Esot√©ricos" 
                className="h-24 w-auto"
              />
            </div>
            <div>
              <CardTitle className="text-2xl font-bold text-gray-800">
                Conselhos Esot√©ricos
              </CardTitle>
              <CardDescription className="text-gray-600">
                Junte-se √† nossa comunidade espiritual
              </CardDescription>
            </div>
          </CardHeader>

          <CardContent className="space-y-6">
            {error && (
              <Alert className="border-red-200 bg-red-50">
                <AlertDescription className="text-red-800">
                  {error}
                </AlertDescription>
              </Alert>
            )}

            {success && (
              <Alert className="border-amber-200 bg-amber-50">
                <CheckCircle2 className="w-4 h-4 text-amber-600" />
                <AlertDescription className="text-amber-800">
                  {success}
                </AlertDescription>
              </Alert>
            )}

            {/* Sele√ß√£o de Tipo de Conta */}
            <div className="space-y-3">
              <Label className="text-sm font-medium text-gray-700">
                Tipo de Conta
              </Label>
              <div className="grid grid-cols-2 gap-3">
                {Object.entries(roleConfig).map(([role, config]) => {
                  const IconComponent = config.icon;
                  const isSelected = selectedRole === role;

                  return (
                    <button
                      key={role}
                      onClick={() => setSelectedRole(role)}
                      className={`p-4 rounded-xl border-2 text-center transition-all duration-200 ${
                        isSelected
                          ? `${config.color} text-white ${config.borderColor}`
                          : `bg-white text-gray-600 border-gray-200 hover:border-gray-300`
                      }`}
                    >
                      <IconComponent className="w-8 h-8 mx-auto mb-2" />
                      <div className="font-medium">{config.title}</div>
                      <div className="text-xs mt-1 opacity-90">
                        {config.description}
                      </div>
                    </button>
                  );
                })}
              </div>
            </div>

            {/* Benef√≠cios do tipo selecionado */}
            <div className={`p-4 rounded-lg border ${currentConfig.borderColor} bg-opacity-5 ${currentConfig.color}`}>
              <h4 className={`font-medium ${currentConfig.textColor} mb-2`}>
                Benef√≠cios como {currentConfig.title}:
              </h4>
              <ul className="text-sm text-gray-600 space-y-1">
                {currentConfig.benefits.map((benefit, index) => (
                  <li key={index} className="flex items-center">
                    <CheckCircle2 className={`w-3 h-3 mr-2 ${currentConfig.textColor}`} />
                    {benefit}
                  </li>
                ))}
              </ul>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="name">Nome Completo</Label>
                <Input
                  id="name"
                  type="text"
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  placeholder="Seu nome completo"
                  required
                  className="h-11"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  placeholder="seu@email.com"
                  required
                  className="h-11"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="cpf">CPF</Label>
                <Input
                  id="cpf"
                  type="text"
                  value={formData.cpf}
                  onChange={(e) => {
                    const cpf = e.target.value.replace(/\D/g, '');
                    const formattedCpf = cpf.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
                    handleInputChange('cpf', formattedCpf);
                  }}
                  placeholder="000.000.000-00"
                  maxLength={14}
                  required
                  className="h-11"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="phone">Telefone</Label>
                <Input
                  id="phone"
                  type="text"
                  value={formData.phone}
                  onChange={(e) => {
                    const phone = e.target.value.replace(/\D/g, '');
                    const formattedPhone = phone.replace(/(\d{2})(\d{4,5})(\d{4})/, '($1) $2-$3');
                    handleInputChange('phone', formattedPhone);
                  }}
                  placeholder="(11) 99999-9999"
                  maxLength={15}
                  required
                  className="h-11"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="password">Senha</Label>
                <div className="relative">
                  <Input
                    id="password"
                    type={showPassword ? 'text' : 'password'}
                    value={formData.password}
                    onChange={(e) => handleInputChange('password', e.target.value)}
                    placeholder="Crie uma senha forte"
                    required
                    className="h-11 pr-10"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  >
                    {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                  </button>
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="confirmPassword">Confirmar Senha</Label>
                <div className="relative">
                  <Input
                    id="confirmPassword"
                    type={showConfirmPassword ? 'text' : 'password'}
                    value={formData.confirmPassword}
                    onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
                    placeholder="Confirme sua senha"
                    required
                    className="h-11 pr-10"
                  />
                  <button
                    type="button"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  >
                    {showConfirmPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                  </button>
                </div>
              </div>

              {/* Campos espec√≠ficos para consultores */}
              {selectedRole === 'consultor' && (
                <>
                  <div className="space-y-2">
                    <Label htmlFor="specialty">Especialidade</Label>
                    <Select value={formData.specialty} onValueChange={(value) => handleInputChange('specialty', value)}>
                      <SelectTrigger className="h-11">
                        <SelectValue placeholder="Selecione sua especialidade" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="tarot">Tarot</SelectItem>
                        <SelectItem value="astrologia">Astrologia</SelectItem>
                        <SelectItem value="numerologia">Numerologia</SelectItem>
                        <SelectItem value="mediunidade">Mediunidade</SelectItem>
                        <SelectItem value="runas">Runas</SelectItem>
                        <SelectItem value="oraculos">Or√°culos dos Anjos</SelectItem>
                        <SelectItem value="cristaloterapia">Cristaloterapia</SelectItem>
                        <SelectItem value="reiki">Reiki</SelectItem>
                        <SelectItem value="terapia-holistico">Terapia Hol√≠stica</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="experience">Anos de Experi√™ncia</Label>
                    <Select value={formData.experience} onValueChange={(value) => handleInputChange('experience', value)}>
                      <SelectTrigger className="h-11">
                        <SelectValue placeholder="Selecione sua experi√™ncia" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="1-2">1-2 anos</SelectItem>
                        <SelectItem value="3-5">3-5 anos</SelectItem>
                        <SelectItem value="6-10">6-10 anos</SelectItem>
                        <SelectItem value="10+">Mais de 10 anos</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="description">Descri√ß√£o Profissional</Label>
                    <Textarea
                      id="description"
                      value={formData.description}
                      onChange={(e) => handleInputChange('description', e.target.value)}
                      placeholder="Descreva sua experi√™ncia, m√©todos e abordagem..."
                      rows={4}
                      className="resize-none"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="pricePerMinute">Pre√ßo por Minuto (R$)</Label>
                    <Input
                      id="pricePerMinute"
                      type="number"
                      step="0.50"
                      min="1.00"
                      value={formData.pricePerMinute}
                      onChange={(e) => handleInputChange('pricePerMinute', e.target.value)}
                      placeholder="5.00"
                      required
                      className="h-11"
                    />
                  </div>
                </>
              )}

              <Button
                type="submit"
                disabled={isLoading}
                className={`w-full h-11 font-semibold ${currentConfig.color} ${currentConfig.hoverColor} text-white`}
              >
                {isLoading ? 'Criando conta...' : `Criar conta como ${currentConfig.title}`}
              </Button>
            </form>

            {selectedRole === 'consultor' && (
              <div className="text-xs text-gray-500 bg-purple-50 p-3 rounded-lg border border-purple-200">
                <strong>Importante:</strong> Contas de consultor passam por um processo de aprova√ß√£o. 
                Voc√™ receber√° um email quando sua conta for ativada.
              </div>
            )}

            <div className="text-center">
              <div className="text-sm text-gray-600">
                J√° tem uma conta?{' '}
                <button
                  onClick={() => setLocation('/login')}
                  className="text-blue-800 font-medium hover:underline"
                >
                  Fa√ßa login
                </button>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Cadastro;

client/src/pages/Chat.tsx.content:
import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { Send, Phone, Video, MoreVertical, Paperclip, Smile, MessageCircle } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { useRoute } from "wouter";

interface Message {
  id: string;
  content: string;
  timestamp: Date;
  sender: 'user' | 'consultant';
  type: 'text' | 'audio' | 'image';
  status: 'sent' | 'delivered' | 'read';
}

export default function Chat() {
  const [match, params] = useRoute("/chat/:consultantId");
  const consultantId = params?.consultantId || "1";
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const { data: consultant } = useQuery({
    queryKey: [`/api/consultants/${consultantId}`],
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Simular conex√£o WebSocket para chat em tempo real
  useEffect(() => {
    // Aqui seria a conex√£o real com WebSocket
    const mockMessages: Message[] = [
      {
        id: '1',
        content: 'Ol√°! Obrigada por escolher uma consulta comigo. Como posso te ajudar hoje?',
        timestamp: new Date(Date.now() - 300000),
        sender: 'consultant',
        type: 'text',
        status: 'read'
      },
      {
        id: '2',
        content: 'Oi! Gostaria de uma orienta√ß√£o sobre minha vida amorosa.',
        timestamp: new Date(Date.now() - 240000),
        sender: 'user',
        type: 'text',
        status: 'read'
      },
      {
        id: '3',
        content: 'Perfeito! Vou puxar as cartas para voc√™. Me fale um pouco sobre sua situa√ß√£o atual no amor.',
        timestamp: new Date(Date.now() - 180000),
        sender: 'consultant',
        type: 'text',
        status: 'read'
      }
    ];
    setMessages(mockMessages);
  }, []);

  const sendMessage = () => {
    if (!newMessage.trim()) return;

    const message: Message = {
      id: Date.now().toString(),
      content: newMessage,
      timestamp: new Date(),
      sender: 'user',
      type: 'text',
      status: 'sent'
    };

    setMessages(prev => [...prev, message]);
    setNewMessage("");

    // Simular resposta do consultor
    setTimeout(() => {
      setIsTyping(true);
      setTimeout(() => {
        const consultantReply: Message = {
          id: (Date.now() + 1).toString(),
          content: 'Entendi. Deixe-me consultar as cartas para te dar uma orienta√ß√£o mais precisa...',
          timestamp: new Date(),
          sender: 'consultant',
          type: 'text',
          status: 'sent'
        };
        setMessages(prev => [...prev, consultantReply]);
        setIsTyping(false);
      }, 2000);
    }, 1000);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const openWhatsApp = () => {
    const whatsappNumber = consultant?.whatsapp || "5511999999999";
    const consultantName = consultant?.name || "Consultor";
    const message = encodeURIComponent(`Ol√° ${consultantName}, gostaria de uma consulta!`);
    window.open(`https://wa.me/${whatsappNumber}?text=${message}`, '_blank');
  };

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      {/* Header do Chat */}
      <div className="bg-white border-b border-gray-200 p-4 flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <img
            src={consultant?.imageUrl || "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=100"}
            alt={consultant?.name || "Consultor"}
            className="w-12 h-12 rounded-full object-cover"
          />
          <div>
            <h2 className="font-semibold text-gray-800">{consultant?.name || "Consultor Especialista"}</h2>
            <p className="text-sm text-green-500">Online</p>
          </div>
        </div>

        <div className="flex items-center space-x-3">
          <button
            onClick={openWhatsApp}
            className="p-2 bg-green-500 text-white rounded-full hover:bg-green-600 transition-colors"
            title="Continuar no WhatsApp"
          >
            <MessageCircle className="w-5 h-5" />
          </button>
          <button className="p-2 bg-blue-500 text-white rounded-full hover:bg-blue-600 transition-colors">
            <Phone className="w-5 h-5" />
          </button>
          <button className="p-2 bg-purple-500 text-white rounded-full hover:bg-purple-600 transition-colors">
            <Video className="w-5 h-5" />
          </button>
          <button className="p-2 hover:bg-gray-100 rounded-full">
            <MoreVertical className="w-5 h-5 text-gray-600" />
          </button>
        </div>
      </div>

      {/* √Årea de Mensagens */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <motion.div
            key={message.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-xs lg:max-w-md px-4 py-2 rounded-2xl ${
                message.sender === 'user'
                  ? 'bg-primary text-white'
                  : 'bg-white text-gray-800 shadow-sm'
              }`}
            >
              <p className="text-sm">{message.content}</p>
              <p className={`text-xs mt-1 ${
                message.sender === 'user' ? 'text-purple-200' : 'text-gray-500'
              }`}>
                {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </p>
            </div>
          </motion.div>
        ))}

        {isTyping && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="flex justify-start"
          >
            <div className="bg-white rounded-2xl px-4 py-2 shadow-sm">
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
              </div>
            </div>
          </motion.div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input de Mensagem */}
      <div className="bg-white border-t border-gray-200 p-4">
        <div className="flex items-center space-x-3">
          <button className="p-2 hover:bg-gray-100 rounded-full">
            <Paperclip className="w-5 h-5 text-gray-600" />
          </button>

          <div className="flex-1 flex items-center bg-gray-100 rounded-full px-4 py-2">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Digite sua mensagem..."
              className="flex-1 bg-transparent outline-none text-gray-800"
            />
            <button className="p-1 hover:bg-gray-200 rounded-full">
              <Smile className="w-5 h-5 text-gray-600" />
            </button>
          </div>

          <button
            onClick={sendMessage}
            disabled={!newMessage.trim()}
            className="p-2 bg-primary text-white rounded-full hover:bg-primary/90 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        {/* Bot√£o WhatsApp Destacado */}
        <div className="mt-3 text-center">
          <button
            onClick={openWhatsApp}
            className="inline-flex items-center space-x-2 bg-green-500 text-white px-6 py-2 rounded-full hover:bg-green-600 transition-colors"
          >
            <MessageCircle className="w-4 h-4" />
            <span>Continuar conversa no WhatsApp</span>
          </button>
        </div>
      </div>
    </div>
  );
}

