      {/* Modal de Avalia√ß√£o */}
      <Dialog open={isReviewModalOpen} onOpenChange={setIsReviewModalOpen}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>Avaliar Consultor</DialogTitle>
            <DialogDescription>
              Compartilhe sua experi√™ncia com {consultantName}
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleReview} className="space-y-4">
            <div>
              <Label>Avalia√ß√£o</Label>
              <div className="flex gap-1 mt-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <Button
                    key={star}
                    type="button"
                    variant="ghost"
                    size="sm"
                    onClick={() => setReviewForm(prev => ({ ...prev, rating: star }))}
                  >
                    <Star 
                      className={`w-6 h-6 ${
                        star <= reviewForm.rating ? "text-yellow-400 fill-current" : "text-gray-300"
                      }`} 
                    />
                  </Button>
                ))}
              </div>
            </div>

            <div>
              <Label htmlFor="comment">Coment√°rio</Label>
              <Textarea 
                id="comment"
                placeholder="Conte sobre sua experi√™ncia..."
                value={reviewForm.comment}
                onChange={(e) => setReviewForm(prev => ({ ...prev, comment: e.target.value }))}
                required
              />
            </div>

            <DialogFooter>
              <Button type="submit" disabled={reviewMutation.isPending}>
                {reviewMutation.isPending ? "Enviando..." : "Enviar Avalia√ß√£o"}
              </Button>
            </DialogFooter>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

