import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Star, MessageCircle, Video, Phone, Clock, Search, Heart, Eye } from "lucide-react";

import { Link } from "wouter";

interface Consultant {
  id: number;
  name: string;
  specialty: string;
  rating: number;
  reviewCount: number;
  pricePerMinute: number;
  profileImage: string;
  status: 'online' | 'busy' | 'offline';
  description: string;
  experience: string;
  communicationMethods: string[];
}

const ConsultantCard = ({ consultant }: { consultant: Consultant }) => {
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online': return 'bg-green-500';
      case 'busy': return 'bg-yellow-500';
      case 'offline': return 'bg-gray-500';
      default: return 'bg-gray-500';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'online': return 'Online';
      case 'busy': return 'Ocupado';
      case 'offline': return 'Offline';
      default: return 'Indispon√≠vel';
    }
  };

  return (
    <Card className="h-full hover:shadow-lg transition-shadow border border-purple-100">
      <CardHeader className="pb-4">
        <div className="flex items-start gap-4">
          <div className="relative">
            <Avatar className="w-16 h-16">
              <AvatarImage src={consultant.profileImage} alt={consultant.name} />
              <AvatarFallback className="bg-purple-100 text-purple-600">
                {consultant.name.split(' ').map(n => n[0]).join('')}
              </AvatarFallback>
            </Avatar>
            <div className={`absolute -bottom-1 -right-1 w-5 h-5 rounded-full border-2 border-white ${getStatusColor(consultant.status)}`} />
          </div>
          <div className="flex-1 min-w-0">
            <CardTitle className="text-lg text-gray-900 truncate">{consultant.name}</CardTitle>
            <Badge variant="secondary" className="mt-1 bg-purple-100 text-purple-700">
              {consultant.specialty}
            </Badge>
            <div className="flex items-center gap-1 mt-2">
              <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
              <span className="text-sm font-medium">{consultant.rating.toFixed(1)}</span>
              <span className="text-sm text-gray-500">({consultant.reviewCount} avalia√ß√µes)</span>
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="pt-0">
        <p className="text-sm text-gray-600 mb-4 line-clamp-2">{consultant.description}</p>

        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-1">
            <Clock className="w-4 h-4 text-gray-400" />
            <span className="text-sm text-gray-600">{consultant.experience}</span>
          </div>
          <span className="text-lg font-bold text-purple-600">
            R$ {consultant.pricePerMinute.toFixed(2)}/min
          </span>
        </div>

        <div className="flex gap-2 mb-4">
          {consultant.communicationMethods.includes('chat') && (
            <Badge variant="outline" className="text-xs">
              <MessageCircle className="w-3 h-3 mr-1" />
              Chat
            </Badge>
          )}
          {consultant.communicationMethods.includes('video') && (
            <Badge variant="outline" className="text-xs">
              <Video className="w-3 h-3 mr-1" />
              V√≠deo
            </Badge>
          )}
          {consultant.communicationMethods.includes('phone') && (
            <Badge variant="outline" className="text-xs">
              <Phone className="w-3 h-3 mr-1" />
              Telefone
            </Badge>
          )}
        </div>

        <div className="flex gap-2">
          <Link href={`/consultor/${consultant.id}`} className="flex-1">
            <Button variant="outline" size="sm" className="w-full">
              <Eye className="w-4 h-4 mr-2" />
              Ver Perfil
            </Button>
          </Link>

          {consultant.status === 'online' ? (
            <Link href={`/agendar-consulta/${consultant.id}`} className="flex-1">
              <Button size="sm" className="w-full bg-purple-600 hover:bg-purple-700">
                <MessageCircle className="w-4 h-4 mr-2" />
                Consultar
              </Button>
            </Link>
          ) : (
            <Button size="sm" className="flex-1" disabled>
              {getStatusText(consultant.status)}
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default function Consultants() {
  const [searchTerm, setSearchTerm] = useState("");
  const [activeSpecialty, setActiveSpecialty] = useState("all");
  const [activeStatus, setActiveStatus] = useState("all");

  const { data: consultants = [], isLoading } = useQuery({
    queryKey: ["/api/consultants"],
  });

  const specialties = [
    "Tarot",
    "Astrologia", 
    "Vid√™ncia",
    "Numerologia",
    "Cartomancia",
    "Baralho Cigano",
    "Mediunidade",
    "Runas",
    "Cristaloterapia"
  ];

  const consultantList = consultants as Consultant[];
  const filteredConsultants = consultantList.filter((consultant: Consultant) => {
    const matchesSearch = searchTerm === "" || 
      consultant.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      consultant.specialty.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesSpecialty = activeSpecialty === "all" || consultant.specialty === activeSpecialty;
    const matchesStatus = activeStatus === "all" || consultant.status === activeStatus;

    return matchesSearch && matchesSpecialty && matchesStatus;
  });

  const onlineConsultants = filteredConsultants.filter((c: Consultant) => c.status === 'online');
  const topRatedConsultants = filteredConsultants
    .sort((a: Consultant, b: Consultant) => b.rating - a.rating)
    .slice(0, 6);

  return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 via-white to-blue-50">
        {/* Header Section */}
        <div className="bg-gradient-to-r from-purple-600 to-blue-600 text-white py-16">
          <div className="container mx-auto px-4">
            <div className="text-center max-w-3xl mx-auto">
              <h1 className="text-4xl md:text-5xl font-bold mb-6">
                Nossos Consultores Especializados
              </h1>
              <p className="text-xl text-purple-100 mb-8">
                Conecte-se com especialistas em esoterismo e encontre as respostas que procura
              </p>

              {/* Search Bar */}
              <div className="relative max-w-md mx-auto">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                <Input
                  type="text"
                  placeholder="Buscar por nome ou especialidade..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 py-3 text-gray-900"
                />
              </div>
            </div>
          </div>
        </div>

        <div className="container mx-auto px-4 py-8">
          {/* Filters */}
          <div className="mb-8">
            <div className="grid md:grid-cols-2 gap-6">
              <div>
                <h3 className="text-lg font-semibold mb-3 text-gray-900">Especialidades</h3>
                <div className="flex flex-wrap gap-2">
                  <Button
                    variant={activeSpecialty === "all" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setActiveSpecialty("all")}
                    className={activeSpecialty === "all" ? "bg-purple-600 hover:bg-purple-700" : ""}
                  >
                    Todas
                  </Button>
                  {specialties.map((specialty) => (
                    <Button
                      key={specialty}
                      variant={activeSpecialty === specialty ? "default" : "outline"}
                      size="sm"
                      onClick={() => setActiveSpecialty(specialty)}
                      className={activeSpecialty === specialty ? "bg-purple-600 hover:bg-purple-700" : ""}
                    >
                      {specialty}
                    </Button>
                  ))}
                </div>
              </div>

              <div>
                <h3 className="text-lg font-semibold mb-3 text-gray-900">Status</h3>
                <div className="flex flex-wrap gap-2">
                  <Button
                    variant={activeStatus === "all" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setActiveStatus("all")}
                    className={activeStatus === "all" ? "bg-purple-600 hover:bg-purple-700" : ""}
                  >
                    Todos
                  </Button>
                  <Button
                    variant={activeStatus === "online" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setActiveStatus("online")}
                    className={activeStatus === "online" ? "bg-green-600 hover:bg-green-700" : ""}
                  >
                    Online
                  </Button>
                  <Button
                    variant={activeStatus === "busy" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setActiveStatus("busy")}
                    className={activeStatus === "busy" ? "bg-yellow-600 hover:bg-yellow-700" : ""}
                  >
                    Ocupado
                  </Button>
                  <Button
                    variant={activeStatus === "offline" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setActiveStatus("offline")}
                    className={activeStatus === "offline" ? "bg-gray-600 hover:bg-gray-700" : ""}
                  >
                    Offline
                  </Button>
                </div>
              </div>
            </div>
          </div>

          {/* Tabs */}
          <Tabs defaultValue="todos" className="w-full">
            <TabsList className="grid w-full grid-cols-3 mb-8">
              <TabsTrigger value="todos">Todos os Consultores</TabsTrigger>
              <TabsTrigger value="online">Online Agora</TabsTrigger>
              <TabsTrigger value="destaques">Destaques</TabsTrigger>
            </TabsList>

            <TabsContent value="todos">
              {isLoading ? (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(6)].map((_, i) => (
                    <Card key={i} className="h-80 animate-pulse">
                      <CardContent className="p-6">
                        <div className="flex items-center gap-4 mb-4">
                          <div className="w-16 h-16 bg-gray-200 rounded-full" />
                          <div className="flex-1">
                            <div className="h-4 bg-gray-200 rounded mb-2" />
                            <div className="h-3 bg-gray-200 rounded w-1/2" />
                          </div>
                        </div>
                        <div className="space-y-2">
                          <div className="h-3 bg-gray-200 rounded" />
                          <div className="h-3 bg-gray-200 rounded w-3/4" />
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              ) : filteredConsultants.length > 0 ? (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {filteredConsultants.map((consultant: Consultant) => (
                    <ConsultantCard key={consultant.id} consultant={consultant} />
                  ))}
                </div>
              ) : (
                <div className="text-center py-12">
                  <div className="text-gray-400 mb-4">
                    <Search className="w-16 h-16 mx-auto" />
                  </div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    Nenhum consultor encontrado
                  </h3>
                  <p className="text-gray-600">
                    Tente ajustar os filtros ou termo de busca
                  </p>
                </div>
              )}
            </TabsContent>

            <TabsContent value="online">
              {onlineConsultants.length > 0 ? (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {onlineConsultants.map((consultant: Consultant) => (
                    <ConsultantCard key={consultant.id} consultant={consultant} />
                  ))}
                </div>
              ) : (
                <div className="text-center py-12">
                  <div className="text-green-400 mb-4">
                    <MessageCircle className="w-16 h-16 mx-auto" />
                  </div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    Nenhum consultor online
                  </h3>
                  <p className="text-gray-600">
                    Nossos consultores est√£o ocupados no momento. Tente novamente em breve.
                  </p>
                </div>
              )}
            </TabsContent>

            <TabsContent value="destaques">
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {topRatedConsultants.map((consultant: Consultant) => (
                  <ConsultantCard key={consultant.id} consultant={consultant} />
                ))}
              </div>
            </TabsContent>
          </Tabs>
        </div>
      </div>
  );
}

client/src/pages/ConsultasOnline.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { 
  Star, 
  Clock, 
  Users, 
  Video, 
  MessageCircle, 
  Phone, 
  CreditCard,
  Filter,
  Search,
  Circle
} from 'lucide-react';
import { FaWhatsapp } from 'react-icons/fa';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface Consultant {
  id: number;
  name: string;
  specialty: string;
  rating: number;
  totalConsultations: number;
  pricePerMinute: number;
  isOnline: boolean;
  profileImage: string;
  bio: string;
  specialtyAreas: string[];
  communicationMethods: string[];
  currentClients: number;
}

interface ConsultationQueue {
  id: number;
  position: number;
  estimatedWaitTime: number;
}

export default function ConsultasOnline() {
  const [selectedConsultant, setSelectedConsultant] = useState<Consultant | null>(null);
  const [communicationMethod, setCommunicationMethod] = useState<string>('');
  const [maxPricePerMinute, setMaxPricePerMinute] = useState<number>(10);
  const [searchTerm, setSearchTerm] = useState('');
  const [specialtyFilter, setSpecialtyFilter] = useState('');
  const [showBookingDialog, setShowBookingDialog] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Buscar consultores dispon√≠veis
  const { data: consultants = [], isLoading } = useQuery({
    queryKey: ['/api/consultation/available-consultants'],
    refetchInterval: 10000, // Atualizar a cada 10 segundos
  });

  // Buscar estat√≠sticas em tempo real
  const { data: stats } = useQuery({
    queryKey: ['/api/consultation/stats'],
    refetchInterval: 5000,
  });

  // Muta√ß√£o para entrar na fila
  const joinQueueMutation = useMutation({
    mutationFn: (queueData: any) => 
      apiRequest('POST', '/api/consultation/queue', queueData),
    onSuccess: (data: ConsultationQueue) => {
      toast({
        title: "Entrada na fila confirmada",
        description: `Posi√ß√£o: ${data.position}. Tempo estimado: ${data.estimatedWaitTime} min`,
      });
      setShowBookingDialog(false);
      queryClient.invalidateQueries({ queryKey: ['/api/consultation/stats'] });
    },
    onError: (error: any) => {
      toast({
        title: "Erro ao entrar na fila",
        description: error.message || "Tente novamente em alguns segundos",
        variant: "destructive",
      });
    }
  });

  // Muta√ß√£o para criar sala de consulta direta
  const createRoomMutation = useMutation({
    mutationFn: (roomData: any) => 
      apiRequest('POST', '/api/consultation/room', roomData),
    onSuccess: (data: any) => {
      toast({
        title: "Sala criada com sucesso",
        description: "Redirecionando para a consulta...",
      });
      // Redirecionar para a sala de consulta
      window.location.href = `/consultation/${data.roomId}`;
    },
    onError: (error: any) => {
      toast({
        title: "Erro ao criar sala",
        description: error.message || "Tente novamente",
        variant: "destructive",
      });
    }
  });

  const filteredConsultants = consultants.filter((consultant: Consultant) => {
    const matchesSearch = consultant.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         consultant.specialty.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesSpecialty = !specialtyFilter || consultant.specialtyAreas.includes(specialtyFilter);
    const matchesPrice = consultant.pricePerMinute <= maxPricePerMinute;

    return matchesSearch && matchesSpecialty && matchesPrice;
  });

  const handleBookConsultation = () => {
    if (!selectedConsultant || !communicationMethod) {
      toast({
        title: "Informa√ß√µes incompletas",
        description: "Selecione um consultor e m√©todo de comunica√ß√£o",
        variant: "destructive",
      });
      return;
    }

    if (selectedConsultant.isOnline && selectedConsultant.currentClients === 0) {
      // Consultor dispon√≠vel - criar sala diretamente
      createRoomMutation.mutate({
        consultantId: selectedConsultant.id,
        communicationMethod,
        creditsPerMinute: selectedConsultant.pricePerMinute
      });
    } else {
      // Consultor ocupado - entrar na fila
      joinQueueMutation.mutate({
        consultantId: selectedConsultant.id,
        serviceType: selectedConsultant.specialty,
        communicationMethod,
        maxPricePerMinute: selectedConsultant.pricePerMinute
      });
    }
  };

  const getCommunicationIcon = (method: string) => {
    switch (method) {
      case 'video': return <Video className="w-4 h-4" />;
      case 'audio': return <Phone className="w-4 h-4" />;
      case 'chat': return <MessageCircle className="w-4 h-4" />;
      case 'whatsapp': return <FaWhatsapp className="w-4 h-4" />;
      default: return <MessageCircle className="w-4 h-4" />;
    }
  };

  const getMethodLabel = (method: string) => {
    switch (method) {
      case 'video': return 'V√≠deo Chamada';
      case 'audio': return 'Chamada de √Åudio';
      case 'chat': return 'Chat de Texto';
      case 'whatsapp': return 'WhatsApp';
      default: return method;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-purple-500 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50">
      {/* Header */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              Consultas Espirituais Online
            </h1>
            <p className="text-gray-600 max-w-2xl mx-auto">
              Conecte-se instantaneamente com nossos especialistas atrav√©s de chat, v√≠deo, √°udio ou WhatsApp
            </p>
          </div>

          {/* Estat√≠sticas em tempo real */}
          {stats && (
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
              <Card>
                <CardContent className="p-4 text-center">
                  <div className="flex items-center justify-center text-green-600 mb-2">
                    <Circle className="w-5 h-5 mr-2 fill-current" />
                    <span className="font-semibold">{stats?.consultantsOnline || 0}</span>
                  </div>
                  <p className="text-sm text-gray-600">Consultores Online</p>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-4 text-center">
                  <div className="flex items-center justify-center text-blue-600 mb-2">
                    <Users className="w-5 h-5 mr-2" />
                    <span className="font-semibold">{stats.activeConsultations}</span>
                  </div>
                  <p className="text-sm text-gray-600">Consultas Ativas</p>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-4 text-center">
                  <div className="flex items-center justify-center text-purple-600 mb-2">
                    <Clock className="w-5 h-5 mr-2" />
                    <span className="font-semibold">{stats.averageWaitTime}min</span>
                  </div>
                  <p className="text-sm text-gray-600">Tempo M√©dio</p>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-4 text-center">
                  <div className="flex items-center justify-center text-orange-600 mb-2">
                    <Users className="w-5 h-5 mr-2" />
                    <span className="font-semibold">{stats.queueSize}</span>
                  </div>
                  <p className="text-sm text-gray-600">Na Fila</p>
                </CardContent>
              </Card>
            </div>
          )}
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Filtros */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Filter className="w-5 h-5 mr-2" />
              Filtrar Consultores
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div>
                <Label htmlFor="search">Buscar</Label>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <Input
                    id="search"
                    placeholder="Nome ou especialidade..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="specialty">Especialidade</Label>
                <Select value={specialtyFilter} onValueChange={setSpecialtyFilter}>
                  <SelectTrigger>
                    <SelectValue placeholder="Todas especialidades" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">Todas especialidades</SelectItem>
                    <SelectItem value="tarot">Tarot</SelectItem>
                    <SelectItem value="astrologia">Astrologia</SelectItem>
                    <SelectItem value="mediunidade">Mediunidade</SelectItem>
                    <SelectItem value="numerologia">Numerologia</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="price">Pre√ßo m√°ximo por minuto</Label>
                <div className="flex items-center space-x-2">
                  <Input
                    id="price"
                    type="number"
                    min="1"
                    max="20"
                    value={maxPricePerMinute}
                    onChange={(e) => setMaxPricePerMinute(Number(e.target.value))}
                  />
                  <span className="text-sm text-gray-600">R$</span>
                </div>
              </div>

              <div className="flex items-end">
                <Button 
                  variant="outline" 
                  onClick={() => {
                    setSearchTerm('');
                    setSpecialtyFilter('');
                    setMaxPricePerMinute(10);
                  }}
                  className="w-full"
                >
                  Limpar Filtros
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Lista de consultores */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredConsultants.map((consultant: Consultant) => (
            <Card key={consultant.id} className="hover:shadow-lg transition-shadow">
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-12 h-12 rounded-full bg-gradient-to-r from-purple-500 to-blue-500 flex items-center justify-center">
                      <span className="text-white font-semibold">
                        {consultant.name.charAt(0)}
                      </span>
                    </div>
                    <div>
                      <h3 className="font-semibold">{consultant.name}</h3>
                      <p className="text-sm text-gray-600">{consultant.specialty}</p>
                    </div>
                  </div>
                  <div className="flex items-center">
                    {consultant.isOnline ? (
                      <Badge variant="default" className="bg-green-500">
                        Online
                      </Badge>
                    ) : (
                      <Badge variant="secondary">Offline</Badge>
                    )}
                  </div>
                </div>
              </CardHeader>

              <CardContent>
                <p className="text-sm text-gray-600 mb-4 line-clamp-2">
                  {consultant.bio}
                </p>

                <div className="space-y-2 mb-4">
                  <div className="flex items-center justify-between text-sm">
                    <span className="flex items-center">
                      <Star className="w-4 h-4 text-yellow-500 mr-1" />
                      {consultant.rating}
                    </span>
                    <span>{consultant.totalConsultations} consultas</span>
                  </div>

                  <div className="flex items-center justify-between text-sm">
                    <span className="flex items-center">
                      <CreditCard className="w-4 h-4 text-purple-500 mr-1" />
                      R$ {consultant.pricePerMinute}/min
                    </span>
                    <span className="text-gray-600">
                      {consultant.currentClients} cliente(s) ativo(s)
                    </span>
                  </div>
                </div>

                {/* M√©todos de comunica√ß√£o dispon√≠veis */}
                <div className="flex flex-wrap gap-1 mb-4">
                  {consultant.communicationMethods.map((method) => (
                    <Badge key={method} variant="outline" className="text-xs">
                      {getCommunicationIcon(method)}
                      <span className="ml-1">{getMethodLabel(method)}</span>
                    </Badge>
                  ))}
                </div>

                <Dialog open={showBookingDialog && selectedConsultant?.id === consultant.id} 
                        onOpenChange={setShowBookingDialog}>
                  <DialogTrigger asChild>
                    <Button 
                      className="w-full" 
                      onClick={() => setSelectedConsultant(consultant)}
                      disabled={!consultant.isOnline}
                    >
                      {consultant.isOnline ? (
                        consultant.currentClients === 0 ? 'Consultar Agora' : 'Entrar na Fila'
                      ) : (
                        'Indispon√≠vel'
                      )}
                    </Button>
                  </DialogTrigger>

                  <DialogContent>
                    <DialogHeader>
                      <DialogTitle>Iniciar Consulta com {consultant.name}</DialogTitle>
                    </DialogHeader>

                    <div className="space-y-4">
                      <div>
                        <Label htmlFor="communication">M√©todo de Comunica√ß√£o</Label>
                        <Select value={communicationMethod} onValueChange={setCommunicationMethod}>
                          <SelectTrigger>
                            <SelectValue placeholder="Escolha como deseja se comunicar" />
                          </SelectTrigger>
                          <SelectContent>
                            {consultant.communicationMethods.map((method) => (
                              <SelectItem key={method} value={method}>
                                <div className="flex items-center">
                                  {getCommunicationIcon(method)}
                                  <span className="ml-2">{getMethodLabel(method)}</span>
                                </div>
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="bg-gray-50 p-4 rounded-lg">
                        <h4 className="font-semibold mb-2">Resumo da Consulta</h4>
                        <div className="space-y-1 text-sm">
                          <div className="flex justify-between">
                            <span>Consultor:</span>
                            <span>{consultant.name}</span>
                          </div>
                          <div className="flex justify-between">
                            <span>Especialidade:</span>
                            <span>{consultant.specialty}</span>
                          </div>
                          <div className="flex justify-between">
                            <span>Pre√ßo por minuto:</span>
                            <span>R$ {consultant.pricePerMinute}</span>
                          </div>
                          <div className="flex justify-between">
                            <span>Status:</span>
                            <span>
                              {consultant.currentClients === 0 ? 
                                'Dispon√≠vel agora' : 
                                `${consultant.currentClients} na frente`
                              }
                            </span>
                          </div>
                        </div>
                      </div>

                      <div className="flex space-x-2">
                        <Button variant="outline" onClick={() => setShowBookingDialog(false)} className="flex-1">
                          Cancelar
                        </Button>
                        <Button 
                          onClick={handleBookConsultation}
                          disabled={joinQueueMutation.isPending || createRoomMutation.isPending}
                          className="flex-1"
                        >
                          {joinQueueMutation.isPending || createRoomMutation.isPending ? 
                            'Conectando...' : 
                            (consultant.currentClients === 0 ? 'Iniciar Agora' : 'Entrar na Fila')
                          }
                        </Button>
                      </div>
                    </div>
                  </DialogContent>
                </Dialog>
              </CardContent>
            </Card>
          ))}
        </div>

        {filteredConsultants.length === 0 && (
          <Card className="text-center py-12">
            <CardContent>
              <h3 className="text-lg font-semibold mb-2">Nenhum consultor encontrado</h3>
              <p className="text-gray-600 mb-4">
                Tente ajustar os filtros ou volte mais tarde
              </p>
              <Button variant="outline" onClick={() => {
                setSearchTerm('');
                setSpecialtyFilter('');
                setMaxPricePerMinute(10);
              }}>
                Limpar Filtros
              </Button>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/ConsultasOnlineReal.tsx.content:
import React, { useState } from "react";
import { motion } from "framer-motion";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { 
  Star, 
  Clock, 
  Users, 
  Search,
  Filter,
  MessageCircle,
  Video,
  Phone,
  Zap,
  CheckCircle,
  Calendar,
  CreditCard
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface Consultant {
  id: number;
  name: string;
  title: string;
  description: string;
  pricePerMinute: number;
  rating: number;
  reviewCount: number;
  imageUrl: string;
  whatsapp: string;
  specialty: string;
  isActive: boolean;
  status: string;
}

export default function ConsultasOnlineReal() {
  const [selectedSpecialty, setSelectedSpecialty] = useState('all');
  const [selectedMethod, setSelectedMethod] = useState('chat');
  const [maxPrice, setMaxPrice] = useState('');
  const [specialRequests, setSpecialRequests] = useState('');
  const [selectedConsultant, setSelectedConsultant] = useState<Consultant | null>(null);
  const { toast } = useToast();

  // Fetch consultores online do PostgreSQL
  const { data: consultants = [], isLoading } = useQuery({
    queryKey: ['/api/consultants'],
    select: (data: any[]) => data.filter((c: Consultant) => c.status === 'online')
  });

  // Fetch user credits
  const { data: userCredits } = useQuery({
    queryKey: ['/api/auth/user'],
    select: (user: any) => user?.credits || 0
  });

  // Mutation para iniciar consulta
  const startConsultationMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/consultations/start", data);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Consulta Iniciada",
        description: "Voc√™ foi conectado ao consultor. Redirecionando...",
      });
      // Redirecionar para sala de consulta
      window.location.href = `/consultation/${data.roomId}`;
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Falha ao iniciar consulta. Tente novamente.",
        variant: "destructive",
      });
    }
  });

  const filteredConsultants = consultants.filter((c: Consultant) => {
    const matchesSpecialty = selectedSpecialty === 'all' || c.specialty.toLowerCase() === selectedSpecialty.toLowerCase();
    const matchesPrice = !maxPrice || c.pricePerMinute <= parseFloat(maxPrice);
    return matchesSpecialty && matchesPrice;
  });

  const startConsultation = async (consultant: Consultant) => {
    if (!userCredits || userCredits < consultant.pricePerMinute * 10) {
      toast({
        title: "Cr√©ditos Insuficientes",
        description: "Voc√™ precisa de mais cr√©ditos para esta consulta.",
        variant: "destructive",
      });
      return;
    }

    startConsultationMutation.mutate({
      consultantId: consultant.id,
      serviceType: consultant.specialty,
      communicationMethod: selectedMethod,
      specialRequests,
      maxPricePerMinute: consultant.pricePerMinute
    });
  };

  const getMethodIcon = (method: string) => {
    switch (method) {
      case 'chat': return <MessageCircle className="w-4 h-4" />;
      case 'video': return <Video className="w-4 h-4" />;
      case 'phone': return <Phone className="w-4 h-4" />;
      default: return <MessageCircle className="w-4 h-4" />;
    }
  };

  const getMethodText = (method: string) => {
    switch (method) {
      case 'chat': return 'Chat';
      case 'video': return 'Videochamada';
      case 'phone': return 'Telefone';
      default: return 'Chat';
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-purple-50">
        <div className="container mx-auto px-4 py-8">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="animate-pulse">
                <div className="bg-gray-300 h-48 rounded-lg mb-4"></div>
                <div className="h-4 bg-gray-300 rounded mb-2"></div>
                <div className="h-3 bg-gray-300 rounded w-2/3"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-purple-50">
      {/* Header */}
      <section className="bg-gradient-to-r from-indigo-900 via-purple-900 to-indigo-900 text-white py-20">
        <div className="container mx-auto px-4 text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <div className="inline-flex items-center justify-center w-16 h-16 bg-amber-400 rounded-full mb-6">
              <Zap className="w-8 h-8 text-amber-900" />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold mb-4">
              Consultas Online
            </h1>
            <p className="text-xl text-indigo-100 max-w-2xl mx-auto">
              Conecte-se instantaneamente com nossos consultores especializados
            </p>
            <div className="mt-6 flex items-center justify-center gap-6 text-sm">
              <div className="flex items-center">
                <CheckCircle className="w-5 h-5 text-green-400 mr-2" />
                <span>{consultants.length} consultores online</span>
              </div>
              <div className="flex items-center">
                <Clock className="w-5 h-5 text-blue-400 mr-2" />
                <span>Resposta em segundos</span>
              </div>
              <div className="flex items-center">
                <CreditCard className="w-5 h-5 text-amber-400 mr-2" />
                <span>Seus cr√©ditos: {userCredits || 0}</span>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Filtros */}
      <section className="py-8 bg-white shadow-sm">
        <div className="container mx-auto px-4">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center text-lg">
                  <Filter className="w-5 h-5 mr-2 text-indigo-600" />
                  Configurar Consulta
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                  {/* Especialidade */}
                  <div>
                    <Label htmlFor="specialty">Especialidade</Label>
                    <Select value={selectedSpecialty} onValueChange={setSelectedSpecialty}>
                      <SelectTrigger>
                        <SelectValue placeholder="Escolha a especialidade" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">Todas</SelectItem>
                        <SelectItem value="tarot">Tarot</SelectItem>
                        <SelectItem value="astrologia">Astrologia</SelectItem>
                        <SelectItem value="mediunidade">Mediunidade</SelectItem>
                        <SelectItem value="numerologia">Numerologia</SelectItem>
                        <SelectItem value="cristaloterapia">Cristaloterapia</SelectItem>
                        <SelectItem value="runas">Runas</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  {/* M√©todo */}
                  <div>
                    <Label htmlFor="method">M√©todo de Comunica√ß√£o</Label>
                    <Select value={selectedMethod} onValueChange={setSelectedMethod}>
                      <SelectTrigger>
                        <SelectValue placeholder="Como quer conversar?" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="chat">Chat de Texto</SelectItem>
                        <SelectItem value="video">Videochamada</SelectItem>
                        <SelectItem value="phone">Liga√ß√£o Telef√¥nica</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  {/* Pre√ßo M√°ximo */}
                  <div>
                    <Label htmlFor="maxPrice">Pre√ßo M√°ximo (R$/min)</Label>
                    <Input
                      id="maxPrice"
                      type="number"
                      placeholder="Ex: 4.00"
                      value={maxPrice}
                      onChange={(e) => setMaxPrice(e.target.value)}
                      min="0"
                      step="0.10"
                    />
                  </div>

                  {/* Bot√£o de Busca */}
                  <div className="flex items-end">
                    <Button className="w-full bg-indigo-600 hover:bg-indigo-700">
                      <Search className="w-4 h-4 mr-2" />
                      Buscar
                    </Button>
                  </div>
                </div>

                {/* Pedidos Especiais */}
                <div className="mt-4">
                  <Label htmlFor="requests">Pedidos Especiais (Opcional)</Label>
                  <Textarea
                    id="requests"
                    placeholder="Descreva brevemente o que gostaria de consultar..."
                    value={specialRequests}
                    onChange={(e) => setSpecialRequests(e.target.value)}
                    rows={2}
                  />
                </div>
              </CardContent>
            </Card>
          </motion.div>
        </div>
      </section>

      {/* Lista de Consultores Online */}
      <section className="py-16">
        <div className="container mx-auto px-4">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="mb-8"
          >
            <div className="flex justify-between items-center">
              <h2 className="text-2xl md:text-3xl font-bold text-gray-800">
                {filteredConsultants.length} consultores online agora
              </h2>
              <div className="flex items-center gap-2 text-green-600">
                <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                <span className="font-semibold">Ao vivo</span>
              </div>
            </div>
          </motion.div>

          {filteredConsultants.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredConsultants.map((consultant: Consultant, index: number) => (
                <motion.div
                  key={consultant.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                >
                  <Card className="h-full hover:shadow-xl transition-all duration-300 overflow-hidden border-l-4 border-green-500">
                    <CardHeader className="pb-3">
                      <div className="flex items-start justify-between">
                        <div className="flex items-center space-x-3">
                          <div className="relative">
                            <div className="w-12 h-12 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-full flex items-center justify-center text-white font-bold">
                              {consultant.name.split(' ').map(n => n[0]).join('').substring(0, 2)}
                            </div>
                            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-white"></div>
                          </div>
                          <div>
                            <CardTitle className="text-lg font-bold text-gray-900">
                              {consultant.name}
                            </CardTitle>
                            <p className="text-sm text-gray-600">{consultant.title}</p>
                          </div>
                        </div>
                        <Badge variant="secondary" className="bg-green-100 text-green-800">
                          {consultant.specialty}
                        </Badge>
                      </div>
                    </CardHeader>

                    <CardContent className="space-y-4">
                      {/* Avalia√ß√£o */}
                      <div className="flex items-center space-x-1">
                        <Star className="w-4 h-4 fill-amber-400 text-amber-400" />
                        <span className="font-semibold">{consultant.rating}</span>
                        <span className="text-sm text-gray-600">({consultant.reviewCount} avalia√ß√µes)</span>
                      </div>

                      {/* Descri√ß√£o */}
                      <p className="text-sm text-gray-600 leading-relaxed">
                        {consultant.description.substring(0, 100)}...
                      </p>

                      {/* Pre√ßo e Tempo */}
                      <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                        <div>
                          <span className="text-lg font-bold text-indigo-600">
                            R$ {consultant.pricePerMinute.toFixed(2)}
                          </span>
                          <span className="text-sm text-gray-600">/minuto</span>
                        </div>
                        <div className="flex items-center text-sm text-gray-600">
                          <Clock className="w-4 h-4 mr-1" />
                          <span>Resposta imediata</span>
                        </div>
                      </div>

                      {/* Bot√£o de A√ß√£o */}
                      <div className="space-y-2">
                        <Button
                          onClick={() => startConsultation(consultant)}
                          disabled={startConsultationMutation.isPending}
                          className="w-full bg-green-600 hover:bg-green-700 flex items-center justify-center"
                        >
                          {startConsultationMutation.isPending ? (
                            <div className="flex items-center">
                              <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                              Conectando...
                            </div>
                          ) : (
                            <div className="flex items-center">
                              {getMethodIcon(selectedMethod)}
                              <span className="ml-2">Iniciar {getMethodText(selectedMethod)}</span>
                            </div>
                          )}
                        </Button>

                        <div className="text-xs text-center text-gray-500">
                          Custo estimado: R$ {(consultant.pricePerMinute * 10).toFixed(2)} (10 min)
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                </motion.div>
              ))}
            </div>
          ) : (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-16"
            >
              <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-6">
                <Users className="w-12 h-12 text-gray-400" />
              </div>
              <h3 className="text-xl font-semibold text-gray-700 mb-2">
                Nenhum consultor online no momento
              </h3>
              <p className="text-gray-600 mb-6">
                Tente ajustar os filtros ou aguarde alguns instantes
              </p>
              <Button 
                onClick={() => window.location.reload()}
                className="bg-indigo-600 hover:bg-indigo-700"
              >
                Atualizar Lista
              </Button>
            </motion.div>
          )}
        </div>
      </section>

      {/* Call to Action */}
      <section className="py-16 bg-gradient-to-r from-indigo-900 to-purple-900 text-white">
        <div className="container mx-auto px-4 text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="text-3xl font-bold mb-4">
              Precisa de mais cr√©ditos?
            </h2>
            <p className="text-indigo-100 mb-8 max-w-2xl mx-auto">
              Compre cr√©ditos e tenha acesso imediato a consultas ilimitadas com nossos especialistas
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Button 
                onClick={() => window.location.href = '/comprar/creditos'}
                size="lg" 
                className="bg-amber-500 hover:bg-amber-600 text-amber-900 font-semibold"
              >
                <CreditCard className="w-5 h-5 mr-2" />
                Comprar Cr√©ditos
              </Button>
              <Button 
                onClick={() => window.location.href = '/consultores'}
                size="lg" 
                variant="outline" 
                className="border-white text-white hover:bg-white hover:text-indigo-900"
              >
                <Calendar className="w-5 h-5 mr-2" />
                Agendar Consulta
              </Button>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
}

client/src/pages/ConsultationLive.tsx.content:
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useQuery } from "@tanstack/react-query";
import { Video, MessageCircle, Phone, Clock, Star, Users } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

export default function ConsultationLive() {
  const [activeRoom, setActiveRoom] = useState<string | null>(null);
  const [messages, setMessages] = useState<any[]>([]);
  const [isConnected, setIsConnected] = useState(false);

  const { data: activeConsultations } = useQuery({
    queryKey: ["/api/consultations/active"],
  });

  const { data: consultationStats } = useQuery({
    queryKey: ["/api/consultation-stats"],
  });

  const startConsultation = (roomId: string, type: 'video' | 'chat' | 'phone') => {
    setActiveRoom(roomId);
    setIsConnected(true);
    // Implementar l√≥gica de conex√£o WebSocket/WebRTC
  };

  const sendMessage = (message: string) => {
    const newMessage = {
      id: Date.now(),
      text: message,
      sender: 'client',
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, newMessage]);
