              </Card>
            </div>

            {/* Chat lateral */}
            <div>
              <Card className="h-96">
                <CardContent className="p-4 h-full flex flex-col">
                  <h4 className="font-semibold mb-4">Chat da Consulta</h4>
                  <div className="flex-1 overflow-y-auto mb-4 space-y-2">
                    {messages.map((msg) => (
                      <div
