import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Star, MessageCircle, Video, Phone, Clock, Search, Eye } from "lucide-react";
import { Link } from "wouter";

interface Consultant {
  id: number;
  name: string;
  specialty: string;
  rating: string;
  reviewCount: number;
  pricePerMinute: string;
  imageUrl: string;
  status: 'online' | 'busy' | 'offline';
  description: string;
  title: string;
  whatsapp: string;
}

const ConsultantCard = ({ consultant }: { consultant: Consultant }) => {
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online': return 'bg-green-500';
      case 'busy': return 'bg-yellow-500';
      case 'offline': return 'bg-gray-500';
      default: return 'bg-gray-500';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'online': return 'Online';
      case 'busy': return 'Ocupado';
      case 'offline': return 'Offline';
      default: return 'Indispon√≠vel';
    }
  };

  return (
    <Card className="h-full hover:shadow-lg transition-shadow border border-purple-100">
      <CardHeader className="pb-4">
        <div className="flex items-start gap-4">
          <div className="relative">
            <Avatar className="w-16 h-16">
              <AvatarImage src={consultant.imageUrl} alt={consultant.name} />
              <AvatarFallback className="bg-purple-100 text-purple-600">
                {consultant.name.split(' ').map(n => n[0]).join('')}
              </AvatarFallback>
            </Avatar>
            <div className={`absolute -bottom-1 -right-1 w-5 h-5 rounded-full border-2 border-white ${getStatusColor(consultant.status)}`} />
          </div>
          <div className="flex-1 min-w-0">
            <CardTitle className="text-lg text-gray-900 truncate">{consultant.name}</CardTitle>
            <Badge variant="secondary" className="mt-1 bg-purple-100 text-purple-700">
              {consultant.specialty}
            </Badge>
            <div className="flex items-center gap-1 mt-2">
              <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
              <span className="text-sm font-medium">{parseFloat(consultant.rating).toFixed(1)}</span>
              <span className="text-sm text-gray-500">({consultant.reviewCount} avalia√ß√µes)</span>
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="pt-0">
        <p className="text-sm text-gray-600 mb-4 line-clamp-2">{consultant.description}</p>

        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-1">
            <Clock className="w-4 h-4 text-gray-400" />
            <span className="text-sm text-gray-600">{consultant.title}</span>
          </div>
          <span className="text-lg font-bold text-purple-600">
            R$ {parseFloat(consultant.pricePerMinute).toFixed(2)}/min
          </span>
        </div>

        <div className="flex gap-2 mb-4">
          {consultant.communicationMethods?.includes('chat') && (
            <Badge variant="outline" className="text-xs">
              <MessageCircle className="w-3 h-3 mr-1" />
              Chat
            </Badge>
          )}
          {consultant.communicationMethods?.includes('video') && (
            <Badge variant="outline" className="text-xs">
              <Video className="w-3 h-3 mr-1" />
              V√≠deo
            </Badge>
          )}
          {consultant.communicationMethods?.includes('phone') && (
            <Badge variant="outline" className="text-xs">
              <Phone className="w-3 h-3 mr-1" />
              Telefone
            </Badge>
          )}
        </div>

        <div className="flex gap-2">
          <Link href={`/consultor/${consultant.id}`} className="flex-1">
            <Button variant="outline" size="sm" className="w-full">
              <Eye className="w-4 h-4 mr-2" />
              Ver Perfil
            </Button>
          </Link>

          {consultant.status === 'online' ? (
            <Link href={`/agendar-consulta/${consultant.id}`} className="flex-1">
              <Button size="sm" className="w-full bg-purple-600 hover:bg-purple-700">
                <MessageCircle className="w-4 h-4 mr-2" />
                Consultar
              </Button>
            </Link>
          ) : (
            <Button size="sm" className="flex-1" disabled>
              {getStatusText(consultant.status)}
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

// Dados de exemplo para testar
const mockConsultants: Consultant[] = [
  {
    id: 1,
    name: "Reda√ß√£o Portal",
    specialty: "Tarot",
    rating: 4.8,
    reviewCount: 127,
    pricePerMinute: 3.50,
    profileImage: "",
    status: 'online',
    description: "Especialista em Tarot Rider-Waite com mais de 15 anos de experi√™ncia. Ajudo pessoas a encontrar clareza em quest√µes amorosas e profissionais.",
    experience: "15 anos",
    communicationMethods: ['chat', 'video', 'phone']
  },
  {
    id: 2,
    name: "Jo√£o Santos",
    specialty: "Astrologia",
    rating: 4.9,
    reviewCount: 89,
    pricePerMinute: 4.00,
    profileImage: "",
    status: 'online',
    description: "Astr√≥logo com forma√ß√£o em Astrologia Tradicional e Moderna. Elaboro mapas natais e previs√µes astrol√≥gicas personalizadas.",
    experience: "12 anos",
    communicationMethods: ['chat', 'video']
  },
  {
    id: 3,
    name: "Ana Costa",
    specialty: "Vid√™ncia",
    rating: 4.7,
    reviewCount: 203,
    pricePerMinute: 3.00,
    profileImage: "",
    status: 'busy',
    description: "M√©dium vidente natural, uso minha intui√ß√£o para orientar pessoas em momentos de d√∫vida e decis√µes importantes.",
    experience: "20 anos",
    communicationMethods: ['chat', 'phone']
  }
];

export default function ConsultoresFixed() {
  const [searchTerm, setSearchTerm] = useState("");
  const [activeSpecialty, setActiveSpecialty] = useState("all");
  const [activeStatus, setActiveStatus] = useState("all");

  const { data: consultants = [], isLoading } = useQuery({
    queryKey: ["/api/consultants"],
  });

  const specialties = [
    "Tarot",
    "Astrologia", 
    "Vid√™ncia",
    "Numerologia",
    "Cartomancia",
    "Baralho Cigano",
    "Mediunidade",
    "Runas",
    "Cristaloterapia"
  ];

  // Use dados reais se dispon√≠veis, sen√£o use dados de exemplo
  const consultantList = (consultants as Consultant[]).length > 0 ? (consultants as Consultant[]) : mockConsultants;

  const filteredConsultants = consultantList.filter((consultant: Consultant) => {
    const matchesSearch = searchTerm === "" || 
      consultant.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      consultant.specialty?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesSpecialty = activeSpecialty === "all" || consultant.specialty === activeSpecialty;
    const matchesStatus = activeStatus === "all" || consultant.status === activeStatus;

    return matchesSearch && matchesSpecialty && matchesStatus;
  });

  const onlineConsultants = filteredConsultants.filter((c: Consultant) => c.status === 'online');
  const topRatedConsultants = filteredConsultants
    .sort((a: Consultant, b: Consultant) => b.rating - a.rating)
    .slice(0, 6);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-white to-blue-50">
