                      <div className="grid md:grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="name"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Nome Completo</FormLabel>
                              <FormControl>
                                <Input placeholder="Seu nome" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="email"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Email</FormLabel>
                              <FormControl>
                                <Input placeholder="seu@email.com" type="email" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <div className="grid md:grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="phone"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Telefone</FormLabel>
                              <FormControl>
                                <Input placeholder="(11) 99999-9999" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="subject"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Assunto</FormLabel>
                              <FormControl>
                                <Input placeholder="Como podemos ajudar?" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>

                      <FormField
                        control={form.control}
                        name="message"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Mensagem</FormLabel>
                            <FormControl>
                              <Textarea
                                placeholder="Descreva sua d√∫vida ou como podemos ajud√°-lo..."
                                className="min-h-[120px]"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <Button 
                        type="submit" 
                        className="w-full bg-gradient-to-r from-primary to-secondary hover:shadow-lg transition-shadow"
                        disabled={contactMutation.isPending}
                      >
                        {contactMutation.isPending ? (
                          "Enviando..."
                        ) : (
                          <>
                            <Send className="w-4 h-4 mr-2" />
                            Enviar Mensagem
                          </>
                        )}
                      </Button>
                    </form>
                  </Form>
                </div>
              </motion.div>

              {/* FAQ */}
              <motion.div
                initial={{ opacity: 0, x: 30 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: 0.2 }}
              >
                <h2 className="text-3xl font-bold text-gray-800 mb-8">
                  Perguntas Frequentes
                </h2>

                <div className="space-y-6">
                  {faqs.map((faq, index) => (
                    <div key={index} className="bg-white rounded-xl p-6 shadow-lg">
                      <h3 className="text-lg font-bold text-gray-800 mb-3">
                        {faq.question}
                      </h3>
                      <p className="text-gray-600 leading-relaxed">
                        {faq.answer}
                      </p>
                    </div>
                  ))}
                </div>

                {/* Atendimento R√°pido */}
                <div className="mt-8 bg-gradient-to-r from-primary to-secondary rounded-2xl p-6 text-white">
                  <h3 className="text-xl font-bold mb-3">
                    Precisa de Atendimento Imediato?
                  </h3>
                  <p className="mb-4 opacity-90">
                    Nossos consultores est√£o online agora para ajud√°-lo
                  </p>
                  <a
                    href="/consultores"
                    className="inline-block bg-white text-primary font-bold py-3 px-6 rounded-lg hover:bg-gray-100 transition-colors"
                  >
                    Falar Agora
                  </a>
                </div>
              </motion.div>
            </div>
          </div>
        </section>

        {/* Mapa e Localiza√ß√£o */}
        <section className="py-16 bg-gray-50">
          <div className="container mx-auto px-4">
            <motion.div
              className="text-center mb-12"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
            >
              <h2 className="text-3xl font-bold text-gray-800 mb-4">
                Nossa Localiza√ß√£o
              </h2>
              <p className="text-xl text-gray-600">
                Atendemos todo o Brasil atrav√©s da nossa plataforma online
              </p>
            </motion.div>

            <div className="bg-white rounded-2xl p-8 shadow-xl">
              <div className="aspect-video bg-gradient-to-br from-purple-100 to-blue-100 rounded-xl flex items-center justify-center">
                <div className="text-center">
                  <MapPin className="w-16 h-16 text-primary mx-auto mb-4" />
                  <h3 className="text-2xl font-bold text-gray-800 mb-2">
                    Atendimento Online
                  </h3>
                  <p className="text-gray-600">
                    Consultores dispon√≠veis 24/7 para atend√™-lo onde voc√™ estiver
                  </p>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
  );
}

client/src/pages/CreditsDemo.tsx.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Coins, Gift, CreditCard, Star } from "lucide-react";

interface UserCredits {
  totalCredits: number;
  bonusCredits: number;
  hasReceivedBonus: boolean;
  firstPurchaseCompleted: boolean;
}

export default function CreditsDemo() {
  const [credits, setCredits] = useState<UserCredits | null>(null);
  const [purchaseAmount, setPurchaseAmount] = useState("50");
  const [loading, setLoading] = useState(false);
  const [userId] = useState("1"); // Demo user ID
  const { toast } = useToast();

  const fetchCredits = async () => {
    try {
      const response = await apiRequest("GET", `/api/credits/${userId}`);
      if (response.ok) {
        const data = await response.json();
        setCredits(data);
      }
    } catch (error) {
      console.error("Erro ao buscar cr√©ditos:", error);
    }
  };

  useEffect(() => {
    fetchCredits();
  }, []);

  const handlePurchase = async () => {
    setLoading(true);
    try {
      const response = await apiRequest("POST", "/api/credits/purchase", {
        userId,
        amount: parseFloat(purchaseAmount),
        paymentMethod: "demo"
      });

      if (response.ok) {
        const result = await response.json();

        toast({
          title: "Compra realizada com sucesso!",
          description: result.bonusActivated 
            ? "Cr√©ditos adicionados + 10 minutos de b√¥nus ativados!" 
            : "Cr√©ditos adicionados √† sua conta",
        });

        if (result.bonusActivated) {
          toast({
            title: "üéÅ B√¥nus de Boas-vindas Ativado!",
            description: "Voc√™ ganhou 10 minutos gr√°tis para sua primeira consulta!",
          });
        }

        await fetchCredits();
      } else {
        const error = await response.json();
        toast({
          title: "Erro na compra",
          description: error.error,
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Erro na compra",
        description: "Falha ao processar compra",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-6">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">
            Sistema de Cr√©ditos e B√¥nus
          </h1>
          <p className="text-blue-200">
            Demonstra√ß√£o do sistema baseado em CPF com b√¥nus para novos usu√°rios
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          {/* Saldo Atual */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Coins className="h-5 w-5 text-yellow-500" />
                Seu Saldo
              </CardTitle>
              <CardDescription>
                Cr√©ditos dispon√≠veis para consultas
              </CardDescription>
            </CardHeader>
            <CardContent>
              {credits ? (
                <div className="space-y-4">
                  <div className="flex justify-between items-center p-3 bg-green-50 rounded-lg">
                    <span className="font-medium">Cr√©ditos Principais</span>
                    <span className="text-xl font-bold text-green-600">
                      {credits.totalCredits} min
                    </span>
                  </div>

                  {credits.bonusCredits > 0 && (
                    <div className="flex justify-between items-center p-3 bg-orange-50 rounded-lg">
                      <span className="font-medium flex items-center gap-2">
                        <Gift className="h-4 w-4 text-orange-500" />
                        Cr√©ditos B√¥nus
                      </span>
                      <span className="text-xl font-bold text-orange-600">
                        {credits.bonusCredits} min
                      </span>
                    </div>
                  )}

                  <div className="pt-2 border-t">
                    <div className="flex justify-between items-center">
                      <span className="font-semibold">Total Dispon√≠vel</span>
                      <span className="text-2xl font-bold text-blue-600">
                        {credits.totalCredits + credits.bonusCredits} min
                      </span>
                    </div>
                  </div>

                  <div className="flex gap-2 mt-4">
                    {!credits.firstPurchaseCompleted && (
                      <Badge variant="outline" className="text-green-600 border-green-600">
                        Eleg√≠vel para B√¥nus de Boas-vindas
                      </Badge>
                    )}
                    {credits.hasReceivedBonus && (
                      <Badge variant="outline" className="text-orange-600 border-orange-600">
                        B√¥nus Recebido
                      </Badge>
                    )}
                  </div>
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  Carregando saldo...
                </div>
              )}
            </CardContent>
          </Card>

          {/* Comprar Cr√©ditos */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CreditCard className="h-5 w-5 text-blue-500" />
                Comprar Cr√©ditos
              </CardTitle>
              <CardDescription>
                Adicione minutos √† sua conta
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-2">
                  Quantidade (em minutos)
                </label>
                <Input
                  type="number"
                  value={purchaseAmount}
                  onChange={(e) => setPurchaseAmount(e.target.value)}
                  min="10"
                  step="10"
                />
              </div>

              {credits && !credits.firstPurchaseCompleted && (
                <div className="p-4 bg-gradient-to-r from-orange-50 to-yellow-50 rounded-lg border border-orange-200">
                  <div className="flex items-center gap-2 mb-2">
                    <Star className="h-5 w-5 text-orange-500" />
                    <span className="font-semibold text-orange-800">
                      Oferta Especial de Boas-vindas!
                    </span>
                  </div>
                  <p className="text-sm text-orange-700">
                    Esta √© sua primeira compra! Voc√™ ganhar√° <strong>10 minutos gr√°tis</strong> ap√≥s confirmar a compra.
                  </p>
                </div>
              )}

              <div className="space-y-2 text-sm text-gray-600">
                <div className="flex justify-between">
                  <span>Cr√©ditos:</span>
                  <span>{purchaseAmount} minutos</span>
                </div>
                {credits && !credits.firstPurchaseCompleted && (
                  <div className="flex justify-between text-orange-600 font-medium">
                    <span>B√¥nus:</span>
                    <span>+10 minutos gr√°tis</span>
                  </div>
                )}
                <div className="flex justify-between border-t pt-2 font-semibold">
                  <span>Total:</span>
                  <span>
                    {parseInt(purchaseAmount) + (credits && !credits.firstPurchaseCompleted ? 10 : 0)} minutos
                  </span>
                </div>
              </div>

              <Button 
                onClick={handlePurchase} 
                className="w-full"
                disabled={loading || !purchaseAmount}
              >
                {loading ? "Processando..." : "Comprar Cr√©ditos"}
              </Button>
            </CardContent>
          </Card>
        </div>

        {/* Informa√ß√µes do Sistema */}
        <Card className="mt-6">
          <CardHeader>
            <CardTitle>Sistema Baseado em CPF - Funcionalidades Implementadas</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid md:grid-cols-3 gap-4">
              <div className="text-center p-4 bg-blue-50 rounded-lg">
                <h3 className="font-semibold text-blue-800 mb-2">Cadastro com CPF</h3>
                <p className="text-sm text-blue-600">
                  CPF como chave √∫nica previne cadastros duplicados
                </p>
              </div>

              <div className="text-center p-4 bg-green-50 rounded-lg">
                <h3 className="font-semibold text-green-800 mb-2">Email Obrigat√≥rio</h3>
                <p className="text-sm text-green-600">
                  Valida√ß√£o de email √© obrigat√≥ria no cadastro
                </p>
              </div>

              <div className="text-center p-4 bg-orange-50 rounded-lg">
                <h3 className="font-semibold text-orange-800 mb-2">B√¥nus Autom√°tico</h3>
                <p className="text-sm text-orange-600">
                  10 minutos gr√°tis ativados na primeira compra
                </p>
              </div>
            </div>

            <div className="mt-6 p-4 bg-gray-50 rounded-lg">
              <h4 className="font-semibold mb-2">APIs Implementadas:</h4>
              <ul className="text-sm space-y-1 text-gray-600">
                <li>‚Ä¢ POST /api/auth/register - Cadastro com CPF</li>
                <li>‚Ä¢ POST /api/auth/login - Login com email</li>
                <li>‚Ä¢ POST /api/credits/purchase - Compra de cr√©ditos com b√¥nus</li>
                <li>‚Ä¢ GET /api/credits/:userId - Consulta de saldo</li>
                <li>‚Ä¢ WebSocket /ws - Chat em tempo real</li>
              </ul>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/DataManagement.tsx.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Plus, Edit, Trash2, Users, Star, MessageSquare } from "lucide-react";

interface Consultant {
  id: number;
  name: string;
  title: string;
  description: string;
  pricePerMinute: string;
  rating: string;
  reviewCount: number;
  imageUrl: string;
  whatsapp: string;
  specialty: string;
  isActive: boolean;
  status: string;
}

interface Testimonial {
  id: number;
  content: string;
  authorName: string;
  authorLocation: string;
  authorImageUrl: string;
  rating: number;
  createdAt: string;
}

export default function DataManagement() {
  const [consultants, setConsultants] = useState<Consultant[]>([]);
  const [testimonials, setTestimonials] = useState<Testimonial[]>([]);
  const [loading, setLoading] = useState(false);
  const { toast } = useToast();

  // New consultant form state
  const [newConsultant, setNewConsultant] = useState({
    name: "",
    title: "",
    description: "",
    pricePerMinute: "",
    specialty: "",
    whatsapp: "",
    imageUrl: ""
  });

  // New testimonial form state
  const [newTestimonial, setNewTestimonial] = useState({
    content: "",
    authorName: "",
    authorLocation: "",
    authorImageUrl: "",
    rating: 5
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [consultantsRes, testimonialsRes] = await Promise.all([
        apiRequest("GET", "/api/admin/consultants"),
        apiRequest("GET", "/api/testimonials")
      ]);

      if (consultantsRes.ok) {
        const consultantsData = await consultantsRes.json();
        setConsultants(consultantsData);
      }

      if (testimonialsRes.ok) {
        const testimonialsData = await testimonialsRes.json();
        setTestimonials(testimonialsData);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const addConsultant = async () => {
    setLoading(true);
    try {
      const response = await apiRequest("POST", "/api/admin/consultants", {
        ...newConsultant,
        pricePerMinute: parseFloat(newConsultant.pricePerMinute),
        isActive: true,
        status: "approved"
      });

      if (response.ok) {
        toast({
          title: "Consultor adicionado",
          description: "Novo consultor criado com sucesso",
        });
        setNewConsultant({
          name: "",
          title: "",
          description: "",
          pricePerMinute: "",
          specialty: "",
          whatsapp: "",
          imageUrl: ""
        });
        fetchData();
      } else {
        throw new Error("Falha ao adicionar consultor");
      }
    } catch (error) {
      toast({
        title: "Erro",
        description: "Falha ao adicionar consultor",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const addTestimonial = async () => {
    setLoading(true);
    try {
      const response = await apiRequest("POST", "/api/admin/testimonials", newTestimonial);

      if (response.ok) {
        toast({
          title: "Depoimento adicionado",
          description: "Novo depoimento criado com sucesso",
        });
        setNewTestimonial({
          content: "",
          authorName: "",
          authorLocation: "",
          authorImageUrl: "",
          rating: 5
        });
        fetchData();
      } else {
        throw new Error("Falha ao adicionar depoimento");
      }
    } catch (error) {
      toast({
        title: "Erro",
        description: "Falha ao adicionar depoimento",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const deleteConsultant = async (id: number) => {
    try {
      const response = await apiRequest("DELETE", `/api/admin/consultants/${id}`);
      if (response.ok) {
        toast({
          title: "Consultor removido",
          description: "Consultor deletado com sucesso",
        });
        fetchData();
      }
    } catch (error) {
      toast({
        title: "Erro",
        description: "Falha ao remover consultor",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 p-6">
      <div className="max-w-6xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">
            Gerenciamento de Dados Reais
          </h1>
          <p className="text-blue-200">
            Administra√ß√£o de consultores e depoimentos baseados em dados aut√™nticos do banco PostgreSQL
          </p>
        </div>

        <Tabs defaultValue="consultants" className="space-y-6">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="consultants" className="flex items-center gap-2">
              <Users className="h-4 w-4" />
              Consultores ({consultants.length})
            </TabsTrigger>
            <TabsTrigger value="testimonials" className="flex items-center gap-2">
              <MessageSquare className="h-4 w-4" />
              Depoimentos ({testimonials.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="consultants" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Plus className="h-5 w-5" />
                  Adicionar Novo Consultor
                </CardTitle>
                <CardDescription>
                  Adicione consultores reais ao banco de dados
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid md:grid-cols-2 gap-4">
                  <Input
                    placeholder="Nome completo"
                    value={newConsultant.name}
                    onChange={(e) => setNewConsultant({...newConsultant, name: e.target.value})}
                  />
                  <Input
                    placeholder="T√≠tulo/Especializa√ß√£o"
                    value={newConsultant.title}
                    onChange={(e) => setNewConsultant({...newConsultant, title: e.target.value})}
                  />
                </div>
                <Textarea
                  placeholder="Descri√ß√£o detalhada do consultor"
                  value={newConsultant.description}
                  onChange={(e) => setNewConsultant({...newConsultant, description: e.target.value})}
                />
                <div className="grid md:grid-cols-3 gap-4">
                  <Input
                    type="number"
                    placeholder="Pre√ßo por minuto"
                    value={newConsultant.pricePerMinute}
                    onChange={(e) => setNewConsultant({...newConsultant, pricePerMinute: e.target.value})}
                  />
                  <Input
                    placeholder="Especialidade"
                    value={newConsultant.specialty}
                    onChange={(e) => setNewConsultant({...newConsultant, specialty: e.target.value})}
                  />
                  <Input
                    placeholder="WhatsApp"
                    value={newConsultant.whatsapp}
                    onChange={(e) => setNewConsultant({...newConsultant, whatsapp: e.target.value})}
                  />
                </div>
                <Input
                  placeholder="URL da imagem"
                  value={newConsultant.imageUrl}
                  onChange={(e) => setNewConsultant({...newConsultant, imageUrl: e.target.value})}
                />
                <Button onClick={addConsultant} disabled={loading} className="w-full">
                  {loading ? "Adicionando..." : "Adicionar Consultor"}
                </Button>
              </CardContent>
            </Card>

            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
              {consultants.map((consultant) => (
                <Card key={consultant.id}>
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between mb-3">
                      <div>
                        <h3 className="font-semibold">{consultant.name}</h3>
                        <p className="text-sm text-gray-600">{consultant.title}</p>
                      </div>
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => deleteConsultant(consultant.id)}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                    <p className="text-sm mb-3">{consultant.description}</p>
                    <div className="flex items-center gap-2 mb-2">
                      <Star className="h-4 w-4 text-yellow-500" />
                      <span className="text-sm">{consultant.rating} ({consultant.reviewCount} avalia√ß√µes)</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <Badge variant={consultant.isActive ? "default" : "secondary"}>
                        {consultant.isActive ? "Ativo" : "Inativo"}
                      </Badge>
                      <span className="text-sm font-medium">R$ {consultant.pricePerMinute}/min</span>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </TabsContent>

          <TabsContent value="testimonials" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Plus className="h-5 w-5" />
                  Adicionar Novo Depoimento
                </CardTitle>
                <CardDescription>
                  Adicione depoimentos reais de clientes ao banco de dados
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <Textarea
                  placeholder="Conte√∫do do depoimento"
                  value={newTestimonial.content}
                  onChange={(e) => setNewTestimonial({...newTestimonial, content: e.target.value})}
                />
                <div className="grid md:grid-cols-2 gap-4">
                  <Input
                    placeholder="Nome do autor"
                    value={newTestimonial.authorName}
                    onChange={(e) => setNewTestimonial({...newTestimonial, authorName: e.target.value})}
                  />
                  <Input
                    placeholder="Localiza√ß√£o"
                    value={newTestimonial.authorLocation}
                    onChange={(e) => setNewTestimonial({...newTestimonial, authorLocation: e.target.value})}
                  />
                </div>
                <div className="grid md:grid-cols-2 gap-4">
                  <Input
                    placeholder="URL da foto do autor"
                    value={newTestimonial.authorImageUrl}
                    onChange={(e) => setNewTestimonial({...newTestimonial, authorImageUrl: e.target.value})}
                  />
                  <Input
                    type="number"
                    min="1"
                    max="5"
