import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { 
  Star, 
  Send, 
  Heart, 
  ThumbsUp, 
  Award, 
  MessageSquare,
  CheckCircle,
  Camera,
  Mic
} from "lucide-react";

interface Review {
  id: string;
  clientName: string;
  clientAvatar: string;
  consultantName: string;
  rating: number;
  title: string;
  content: string;
  date: Date;
  serviceType: string;
  helpful: number;
  verified: boolean;
  response?: {
    content: string;
    date: Date;
  };
}

export default function ReviewSystem() {
  const [activeTab, setActiveTab] = useState("leave-review");
  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [reviewTitle, setReviewTitle] = useState("");
  const [reviewContent, setReviewContent] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const consultantInfo = {
    name: "Consultor Especialista",
    avatar: "https://images.unsplash.com/photo-1494790108755-2616b612b5e5?w=150&h=150&fit=crop&crop=face",
    specialty: "Tarot e Astrologia",
    avgRating: 4.9,
    totalReviews: 1247
  };

  const recentReviews: Review[] = [
    {
      id: "1",
      clientName: "Ana Silva",
