          )}
        </div>

        {/* Service Type */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">
            Tipo de Servi√ßo
          </label>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
            {serviceTypes.map((service) => (
              <button
                key={service}
                onClick={() => setSelectedService(service)}
                className={`p-3 rounded-lg border-2 transition-all ${
                  selectedService === service
                    ? 'border-purple-500 bg-purple-50 text-purple-700'
                    : 'border-gray-200 hover:border-purple-300'
                }`}
              >
                {service}
              </button>
            ))}
          </div>
        </div>

        {/* Review Title */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            T√≠tulo da Avalia√ß√£o
          </label>
          <input
            type="text"
            value={reviewTitle}
            onChange={(e) => setReviewTitle(e.target.value)}
            placeholder="Resuma sua experi√™ncia em poucas palavras"
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            maxLength={100}
          />
          <p className="text-right text-xs text-gray-500 mt-1">
            {reviewTitle.length}/100
          </p>
        </div>

        {/* Review Content */}
        <div className="mb-8">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Sua Avalia√ß√£o Detalhada
          </label>
          <textarea
            value={reviewContent}
            onChange={(e) => setReviewContent(e.target.value)}
            placeholder="Conte sobre sua experi√™ncia, o que mais gostou, como se sentiu durante a consulta..."
