      author: {
        name: "Patricia Oliveira",
        location: "Porto Alegre, RS",
        imageUrl: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?q=80&w=1000&auto=format&fit=crop"
      },
      rating: 4,
      service: "Runas",
      date: "2024-01-05"
    },
    {
      id: 6,
      content: "Excelente plataforma! F√°cil de usar e consultores muito qualificados. J√° fiz v√°rias consultas e sempre saio satisfeita.",
      author: {
        name: "Juliana Santos",
        location: "Curitiba, PR",
        imageUrl: "https://images.unsplash.com/photo-1544005313-94ddf0286df2?q=80&w=1000&auto=format&fit=crop"
      },
      rating: 5,
      service: "Tarot",
      date: "2024-01-03"
    }
