}

src/app/chat/page.tsx.content:
"use client";

import { useState, useRef, useEffect } from "react";
import { 
  Send, 
  Phone, 
  Video, 
  MoreHorizontal, 
  Smile, 
  Paperclip,
  Star,
  Clock,
  ArrowLeft,
  AlertCircle
} from "lucide-react";
import Link from "next/link";
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';

interface Message {
  id: number;
  sender: 'user' | 'consultant' | 'system';
  content: string;
  timestamp: Date;
  type: 'text' | 'system';
}

interface ChatSession {
  id: string;
  consultant: {
    name: string;
    image: string;
    specialty: string;
    pricePerMinute: number;
    status: 'online' | 'typing' | 'away';
  };
  startTime: Date;
  duration: number;
  cost: number;
  userCredits: number;
}

// Mock data para demonstra√ß√£o
const mockSession: ChatSession = {
  id: "session_123",
  consultant: {
    name: "Sophia Vidente",
    image: "https://ext.same-assets.com/741558681/1685023612.jpeg",
    specialty: "Vid√™ncia e B√∫zios",
    pricePerMinute: 4.25,
    status: "online"
  },
  startTime: new Date(),
  duration: 0,
  cost: 0,
  userCredits: 125
};

const mockMessages: Message[] = [
  {
    id: 1,
    sender: 'system',
    content: 'Consulta iniciada com Sophia Vidente. Taxa: R$ 4,25/min',
    timestamp: new Date(Date.now() - 300000),
    type: 'system'
  },
  {
    id: 2,
    sender: 'consultant',
    content: 'Ol√°! Sou Sophia, seja muito bem-vinda! Sinto uma energia muito especial ao nosso redor. Como posso te ajudar hoje?',
    timestamp: new Date(Date.now() - 280000),
    type: 'text'
  },
  {
    id: 3,
    sender: 'user',
    content: 'Oi Sophia! Estou passando por um momento dif√≠cil no relacionamento e gostaria de uma orienta√ß√£o.',
    timestamp: new Date(Date.now() - 260000),
    type: 'text'
  },
  {
    id: 4,
    sender: 'consultant',
    content: 'Entendo perfeitamente. Vou consultar os b√∫zios para voc√™. Estou sentindo que h√° muita confus√£o de sentimentos envolvida. Me conta um pouco mais sobre a situa√ß√£o?',
    timestamp: new Date(Date.now() - 240000),
    type: 'text'
  }
];

export default function ChatPage() {
  const [messages, setMessages] = useState<Message[]>(mockMessages);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [session, setSession] = useState<ChatSession>(mockSession);
  const [isSessionActive, setIsSessionActive] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Simular timer da sess√£o
  useEffect(() => {
    if (!isSessionActive) return;

    const timer = setInterval(() => {
      setSession(prev => ({
        ...prev,
        duration: prev.duration + 1,
        cost: (prev.duration + 1) * (prev.consultant.pricePerMinute / 60)
      }));
    }, 1000);

    return () => clearInterval(timer);
  }, [isSessionActive]);

  // Auto-scroll para √∫ltima mensagem
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    if (!newMessage.trim() || !isSessionActive) return;

    const userMessage: Message = {
      id: messages.length + 1,
      sender: 'user',
      content: newMessage,
      timestamp: new Date(),
      type: 'text'
    };

    setMessages(prev => [...prev, userMessage]);
    setNewMessage('');
    setIsTyping(true);

    // Simular resposta do consultor
    setTimeout(() => {
      const consultantMessage: Message = {
        id: messages.length + 2,
        sender: 'consultant',
        content: 'Compreendo sua situa√ß√£o. Os b√∫zios est√£o me mostrando que h√° uma fase de transforma√ß√£o chegando. Preciso que voc√™ me diga se h√° uma terceira pessoa envolvida nessa hist√≥ria?',
        timestamp: new Date(),
        type: 'text'
      };
      setMessages(prev => [...prev, consultantMessage]);
      setIsTyping(false);
    }, 3000);
  };

  const endSession = () => {
    setIsSessionActive(false);
    const systemMessage: Message = {
      id: messages.length + 1,
      sender: 'system',
      content: `Consulta finalizada. Dura√ß√£o: ${Math.floor(session.duration / 60)}:${(session.duration % 60).toString().padStart(2, '0')}. Custo total: R$ ${session.cost.toFixed(2)}`,
      timestamp: new Date(),
      type: 'system'
    };
    setMessages(prev => [...prev, systemMessage]);
  };

  const formatTime = (seconds: number) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 px-4 py-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Link href="/consultores">
              <Button variant="ghost" size="sm">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Voltar
              </Button>
            </Link>

            <div className="flex items-center space-x-3">
              <Avatar className="w-10 h-10">
                <AvatarImage src={session.consultant.image} alt={session.consultant.name} />
                <AvatarFallback>{session.consultant.name.split(' ').map(n => n[0]).join('')}</AvatarFallback>
              </Avatar>
              <div>
                <div className="font-semibold text-dark-blue">{session.consultant.name}</div>
                <div className="text-sm text-gray-600 flex items-center space-x-2">
                  <Badge className="bg-green text-white text-xs">
                    {session.consultant.status === 'online' ? 'Online' : 
                     session.consultant.status === 'typing' ? 'Digitando...' : 'Ausente'}
                  </Badge>
                  <span>‚Ä¢</span>
                  <span>{session.consultant.specialty}</span>
                </div>
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            {/* Timer e Custo */}
            <div className="text-right">
              <div className="font-semibold text-dark-blue flex items-center">
                <Clock className="w-4 h-4 mr-1" />
                {formatTime(session.duration)}
              </div>
              <div className="text-sm text-gray-600">
                R$ {session.cost.toFixed(2)} ‚Ä¢ {session.userCredits} cr√©ditos
              </div>
            </div>

            {/* Actions */}
            <div className="flex space-x-2">
              <Button variant="outline" size="sm">
                <Phone className="w-4 h-4" />
              </Button>
              <Button variant="outline" size="sm">
                <Video className="w-4 h-4" />
              </Button>
              <Button variant="outline" size="sm">
                <MoreHorizontal className="w-4 h-4" />
              </Button>
              <Button 
                onClick={endSession}
                className="bg-red-600 hover:bg-red-700 text-white"
                size="sm"
                disabled={!isSessionActive}
              >
                Finalizar
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Status Bar */}
      {isSessionActive && (
        <div className="bg-green/10 border-b border-green/20 px-4 py-2">
          <div className="flex items-center justify-between text-sm">
            <div className="flex items-center space-x-2 text-green-700">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span>Consulta em andamento</span>
            </div>
            <div className="text-green-700">
              Taxa: R$ {session.consultant.pricePerMinute.toFixed(2)}/min
            </div>
          </div>
        </div>
      )}

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <div key={message.id}>
            {message.type === 'system' ? (
              <div className="flex justify-center">
                <div className="bg-gray-200 text-gray-700 px-3 py-1 rounded-full text-sm flex items-center space-x-2">
                  <AlertCircle className="w-4 h-4" />
                  <span>{message.content}</span>
                </div>
              </div>
            ) : (
              <div className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className="flex items-end space-x-2 max-w-xs lg:max-w-md">
                  {message.sender === 'consultant' && (
                    <Avatar className="w-8 h-8">
                      <AvatarImage src={session.consultant.image} alt={session.consultant.name} />
                      <AvatarFallback className="text-xs">
                        {session.consultant.name.split(' ').map(n => n[0]).join('')}
                      </AvatarFallback>
                    </Avatar>
                  )}

                  <div className={`rounded-lg px-4 py-2 ${
                    message.sender === 'user'
                      ? 'bg-gold text-dark-blue ml-auto'
                      : 'bg-white text-gray-800 border border-gray-200'
                  }`}>
                    <p className="text-sm">{message.content}</p>
                    <p className={`text-xs mt-1 ${
                      message.sender === 'user' ? 'text-dark-blue/70' : 'text-gray-500'
                    }`}>
                      {message.timestamp.toLocaleTimeString('pt-BR', { 
                        hour: '2-digit', 
                        minute: '2-digit' 
                      })}
                    </p>
                  </div>

                  {message.sender === 'user' && (
                    <Avatar className="w-8 h-8">
                      <AvatarFallback className="text-xs bg-gold text-dark-blue">
                        EU
                      </AvatarFallback>
                    </Avatar>
                  )}
                </div>
              </div>
            )}
          </div>
        ))}

        {/* Typing Indicator */}
        {isTyping && (
          <div className="flex justify-start">
            <div className="flex items-end space-x-2">
              <Avatar className="w-8 h-8">
                <AvatarImage src={session.consultant.image} alt={session.consultant.name} />
                <AvatarFallback className="text-xs">
                  {session.consultant.name.split(' ').map(n => n[0]).join('')}
                </AvatarFallback>
              </Avatar>
              <div className="bg-white border border-gray-200 rounded-lg px-4 py-2">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                  <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                </div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <div className="bg-white border-t border-gray-200 p-4">
        {isSessionActive ? (
          <div className="flex items-center space-x-3">
            <Button variant="outline" size="sm">
              <Paperclip className="w-4 h-4" />
            </Button>

            <div className="flex-1 relative">
              <Input
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Digite sua mensagem..."
                className="pr-10"
                onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              />
              <Button 
                variant="ghost" 
                size="sm" 
                className="absolute right-2 top-1/2 transform -translate-y-1/2"
              >
                <Smile className="w-4 h-4" />
              </Button>
            </div>

            <Button 
              onClick={sendMessage}
              className="bg-gold hover:bg-gold/90 text-dark-blue"
              disabled={!newMessage.trim()}
            >
              <Send className="w-4 h-4" />
            </Button>
          </div>
        ) : (
          <div className="text-center py-4">
            <p className="text-gray-600 mb-4">Consulta finalizada</p>
            <div className="flex justify-center space-x-3">
              <Button className="bg-gold hover:bg-gold/90 text-dark-blue">
                <Star className="w-4 h-4 mr-2" />
                Avaliar Consulta
              </Button>
              <Link href="/consultores">
                <Button variant="outline">
                  Nova Consulta
                </Button>
              </Link>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

src/app/dashboard/page.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { User, CreditCard, MessageCircle, Calendar, Star, Settings, LogOut, Bell, Coins } from "lucide-react";

interface UserData {
  id: number;
  name: string;
  email: string;
  phone?: string;
  birthDate?: string;
  credits: number;
  createdAt: string;
}

interface Session {
  id: number;
  consultantName: string;
  date: string;
  duration: number;
  status: string;
  rating?: number;
}

export default function DashboardPage() {
  const [user, setUser] = useState<UserData | null>(null);
  const [sessions, setSessions] = useState<Session[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("overview");
  const router = useRouter();

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (!userData) {
      router.push("/login");
      return;
    }

    try {
      const parsedUser = JSON.parse(userData);
      setUser(parsedUser);
      loadUserSessions(parsedUser.id);
    } catch (error) {
      console.error("Erro ao carregar dados do usu√°rio:", error);
      router.push("/login");
    }
  }, [router]);

  const loadUserSessions = async (userId: number) => {
    try {
      const response = await fetch(`/api/sessions/user/${userId}`);
      if (response.ok) {
        const data = await response.json();
        setSessions(data);
      }
    } catch (error) {
      console.error("Erro ao carregar sess√µes:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    router.push("/");
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("pt-BR", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "completed":
        return "bg-green-100 text-green-800";
      case "active":
        return "bg-blue-100 text-blue-800";
      case "cancelled":
        return "bg-red-100 text-red-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case "completed":
        return "Conclu√≠da";
      case "active":
        return "Ativa";
      case "cancelled":
        return "Cancelada";
      default:
        return "Pendente";
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-800 via-blue-800 to-indigo-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-white"></div>
      </div>
    );
  }

  if (!user) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center">
              <Link href="/" className="text-2xl font-bold text-purple-600">
                Portal Esot√©rico
              </Link>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Coins className="w-5 h-5 text-yellow-500" />
                <span className="font-semibold text-gray-700">{user.credits} cr√©ditos</span>
              </div>
              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 text-gray-600 hover:text-gray-800 transition-colors"
              >
                <LogOut className="w-5 h-5" />
                <span>Sair</span>
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Sidebar */}
          <div className="w-full lg:w-64 bg-white rounded-lg shadow-sm p-6">
            <div className="text-center mb-6">
              <div className="w-20 h-20 bg-gradient-to-br from-purple-500 to-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <User className="w-10 h-10 text-white" />
              </div>
              <h2 className="text-xl font-bold text-gray-800">{user.name}</h2>
              <p className="text-gray-600 text-sm">{user.email}</p>
            </div>

            <nav className="space-y-2">
              <button
                onClick={() => setActiveTab("overview")}
                className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                  activeTab === "overview"
                    ? "bg-purple-100 text-purple-700"
                    : "text-gray-600 hover:bg-gray-100"
                }`}
              >
                <User className="w-5 h-5" />
                <span>Vis√£o Geral</span>
              </button>
              <button
                onClick={() => setActiveTab("sessions")}
                className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                  activeTab === "sessions"
                    ? "bg-purple-100 text-purple-700"
                    : "text-gray-600 hover:bg-gray-100"
                }`}
              >
                <MessageCircle className="w-5 h-5" />
                <span>Minhas Consultas</span>
              </button>
              <button
                onClick={() => setActiveTab("credits")}
                className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                  activeTab === "credits"
                    ? "bg-purple-100 text-purple-700"
                    : "text-gray-600 hover:bg-gray-100"
                }`}
              >
                <CreditCard className="w-5 h-5" />
                <span>Comprar Cr√©ditos</span>
              </button>
              <button
                onClick={() => setActiveTab("profile")}
                className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${
                  activeTab === "profile"
                    ? "bg-purple-100 text-purple-700"
                    : "text-gray-600 hover:bg-gray-100"
                }`}
              >
                <Settings className="w-5 h-5" />
                <span>Perfil</span>
              </button>
            </nav>
          </div>

          {/* Main Content */}
          <div className="flex-1">
            {activeTab === "overview" && (
              <div className="space-y-6">
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-2xl font-bold text-gray-800 mb-6">Painel de Controle</h3>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                    <div className="bg-gradient-to-r from-purple-500 to-purple-600 rounded-lg p-6 text-white">
                      <div className="flex items-center">
                        <Coins className="w-8 h-8 mr-3" />
                        <div>
                          <p className="text-purple-100">Cr√©ditos</p>
                          <p className="text-2xl font-bold">{user.credits}</p>
                        </div>
                      </div>
                    </div>
                    <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-lg p-6 text-white">
                      <div className="flex items-center">
                        <MessageCircle className="w-8 h-8 mr-3" />
                        <div>
                          <p className="text-blue-100">Consultas</p>
                          <p className="text-2xl font-bold">{sessions.length}</p>
                        </div>
                      </div>
                    </div>
                    <div className="bg-gradient-to-r from-green-500 to-green-600 rounded-lg p-6 text-white">
                      <div className="flex items-center">
                        <Star className="w-8 h-8 mr-3" />
                        <div>
                          <p className="text-green-100">Avalia√ß√µes</p>
                          <p className="text-2xl font-bold">{sessions.filter(s => s.rating).length}</p>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="bg-gradient-to-r from-purple-600 to-blue-600 rounded-lg p-6 text-white">
                    <h4 className="text-xl font-bold mb-4">Pronto para uma consulta?</h4>
                    <p className="mb-4">Conecte-se com nossos consultores experientes e encontre as respostas que procura.</p>
                    <Link href="/#consultores">
                      <button className="bg-white text-purple-600 px-6 py-3 rounded-lg font-medium hover:bg-gray-100 transition-colors">
                        Ver Consultores
                      </button>
                    </Link>
                  </div>
                </div>
              </div>
            )}

            {activeTab === "sessions" && (
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-2xl font-bold text-gray-800 mb-6">Minhas Consultas</h3>

                {sessions.length === 0 ? (
                  <div className="text-center py-12">
                    <MessageCircle className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <h4 className="text-lg font-medium text-gray-600 mb-2">Nenhuma consulta ainda</h4>
                    <p className="text-gray-500 mb-6">Comece sua jornada espiritual agora</p>
                    <Link href="/#consultores">
                      <button className="bg-purple-600 text-white px-6 py-3 rounded-lg font-medium hover:bg-purple-700 transition-colors">
                        Consultar Agora
                      </button>
                    </Link>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {sessions.map((session) => (
                      <div key={session.id} className="border rounded-lg p-4 hover:bg-gray-50 transition-colors">
                        <div className="flex justify-between items-start">
                          <div className="flex-1">
                            <h4 className="font-medium text-gray-800">{session.consultantName}</h4>
                            <p className="text-sm text-gray-600">{formatDate(session.date)}</p>
                            <p className="text-sm text-gray-600">{session.duration} minutos</p>
                          </div>
                          <div className="text-right">
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(session.status)}`}>
                              {getStatusText(session.status)}
                            </span>
                            {session.rating && (
                              <div className="flex items-center mt-2">
                                {[...Array(5)].map((_, i) => (
                                  <Star
                                    key={i}
                                    className={`w-4 h-4 ${
                                      i < session.rating! ? "text-yellow-400 fill-current" : "text-gray-300"
                                    }`}
                                  />
                                ))}
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === "credits" && (
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-2xl font-bold text-gray-800 mb-6">Comprar Cr√©ditos</h3>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  <div className="border-2 border-gray-200 rounded-lg p-6 text-center hover:border-purple-500 transition-colors">
                    <div className="text-3xl font-bold text-purple-600 mb-2">100</div>
                    <div className="text-gray-600 mb-4">Cr√©ditos</div>
                    <div className="text-2xl font-bold text-gray-800 mb-4">R$ 50,00</div>
                    <button className="w-full bg-purple-600 text-white py-3 rounded-lg font-medium hover:bg-purple-700 transition-colors">
                      Comprar
                    </button>
                  </div>

                  <div className="border-2 border-purple-500 rounded-lg p-6 text-center bg-purple-50">
                    <div className="text-xs bg-purple-600 text-white px-2 py-1 rounded-full inline-block mb-2">
                      MAIS POPULAR
                    </div>
                    <div className="text-3xl font-bold text-purple-600 mb-2">250</div>
                    <div className="text-gray-600 mb-4">Cr√©ditos</div>
                    <div className="text-2xl font-bold text-gray-800 mb-4">R$ 120,00</div>
                    <button className="w-full bg-purple-600 text-white py-3 rounded-lg font-medium hover:bg-purple-700 transition-colors">
                      Comprar
                    </button>
                  </div>

                  <div className="border-2 border-gray-200 rounded-lg p-6 text-center hover:border-purple-500 transition-colors">
                    <div className="text-3xl font-bold text-purple-600 mb-2">500</div>
                    <div className="text-gray-600 mb-4">Cr√©ditos</div>
                    <div className="text-2xl font-bold text-gray-800 mb-4">R$ 200,00</div>
                    <button className="w-full bg-purple-600 text-white py-3 rounded-lg font-medium hover:bg-purple-700 transition-colors">
                      Comprar
                    </button>
                  </div>
                </div>
              </div>
            )}

            {activeTab === "profile" && (
              <div className="bg-white rounded-lg shadow-sm p-6">
                <h3 className="text-2xl font-bold text-gray-800 mb-6">Meu Perfil</h3>

                <div className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Nome</label>
                      <input
                        type="text"
                        value={user.name}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                        readOnly
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                      <input
                        type="email"
                        value={user.email}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                        readOnly
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Telefone</label>
                      <input
                        type="tel"
                        value={user.phone || ""}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                        readOnly
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Data de Nascimento</label>
                      <input
                        type="date"
                        value={user.birthDate || ""}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                        readOnly
                      />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Membro desde</label>
                    <input
                      type="text"
                      value={formatDate(user.createdAt)}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                      readOnly
                    />
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

