import { Star, MessageCircle, Clock, Shield, Award, ArrowLeft, User, Heart } from "lucide-react";

interface Consultant {
  id: number;
  name: string;
  slug: string;
  title: string;
  description: string;
  pricePerMinute: number;
  rating: number;
  reviewCount: number;
  imageUrl: string;
  specialty: string;
  specialties: string[];
  status: string;
  experience: string;
}

