import { type NextRequest, NextResponse } from "next/server";
import postgres from "postgres";
import bcrypt from "bcryptjs";

const sql = postgres(process.env.DATABASE_URL!);

export async function POST(request: NextRequest) {
  try {
    const { name, email, phone, birthDate, password } = await request.json();

    // Validar campos obrigat√≥rios
    if (!name || !email || !password) {
      return NextResponse.json(
        { error: "Nome, email e senha s√£o obrigat√≥rios" },
        { status: 400 }
      );
    }

    // Verificar se o usu√°rio j√° existe
    const existingUsers = await sql`
      SELECT id FROM users WHERE email = ${email}
    `;

    if (existingUsers.length > 0) {
      return NextResponse.json(
        { error: "Email j√° est√° em uso" },
        { status: 400 }
      );
    }

    // Criptografar senha
    const hashedPassword = await bcrypt.hash(password, 10);

    // Criar usu√°rio
    const newUsers = await sql`
      INSERT INTO users (name, email, phone, birth_date, password, credits, role)
      VALUES (${name}, ${email}, ${phone || null}, ${birthDate || null}, ${hashedPassword}, 50, 'user')
      RETURNING id, name, email, phone, birth_date as "birthDate", credits, role, created_at as "createdAt"
    `;

    const newUser = newUsers[0];

    // Remover senha da resposta
    const { password: _, ...userWithoutPassword } = newUser;

    return NextResponse.json(
      { message: "Usu√°rio criado com sucesso", user: userWithoutPassword },
      { status: 201 }
    );
  } catch (error) {
    console.error("Erro ao criar usu√°rio:", error);
    return NextResponse.json(
      { error: "Erro interno do servidor" },
      { status: 500 }
    );
  }
}

src/app/api/consultants/slug/[slug]/rout.content:
import { type NextRequest, NextResponse } from "next/server";
import postgres from "postgres";

const sql = postgres(process.env.DATABASE_URL!);

export async function GET(
  request: NextRequest,
  { params }: { params: { slug: string } }
) {
  try {
    const slug = params.slug;

    if (!slug) {
      return NextResponse.json(
        { error: "Slug √© obrigat√≥rio" },
        { status: 400 }
      );
    }

    // Buscar consultor pelo slug
    const consultants = await sql`
      SELECT 
        id, name, slug, title, description, 
        price_per_minute as "pricePerMinute",
        rating, review_count as "reviewCount",
        image_url as "imageUrl", specialty, specialties,
        status, experience
      FROM consultants 
      WHERE slug = ${slug}
    `;

    if (consultants.length === 0) {
      return NextResponse.json(
        { error: "Consultor n√£o encontrado" },
        { status: 404 }
      );
    }

    const consultant = consultants[0];

    // Converter pricePerMinute para n√∫mero
    consultant.pricePerMinute = Number.parseFloat(consultant.pricePerMinute);
    consultant.rating = Number.parseFloat(consultant.rating);

    return NextResponse.json(consultant);
  } catch (error) {
    console.error("Erro ao buscar consultor:", error);
    return NextResponse.json(
      { error: "Erro interno do servidor" },
      { status: 500 }
    );
  }
}
